import React, { useState } from "react";
import { StyleSheet, Text, TouchableOpacity, View, Image } from "react-native";
import * as ImagePicker from "expo-image-picker";
import { Ionicons } from "@expo/vector-icons";

const MAX_FILE_SIZE = 5 * 1024 * 1024; // 5 MB (in bytes)
const selectImage = () => {
  const [images, setImages] = useState([]);
  //   const(images, setImages) = useState(null);

  //   const pickImageAsync = async () => {
  //     let result = await ImagePicker.launchImageLibraryAsync({
  //       allowsEditing: true,
  //       quality: 1,
  //     });

  //     if (!result.cancelled) {
  //       setImage(result.uri);
  //     } else {
  //       alert("You did not select any image.");
  //     }
  //   };
  const handleImagePick = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();

    if (status !== "granted") {
      alert("Sorry, we need camera roll permissions to make this work!");
      return;
    }

    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
      multiple: true, // Enable multiple image selection
    });

    console.log("Image Picker Result:", result);

    if (!result.cancelled) {
      // Update the images state variable with selected image URIs
      setImages(result.assets.map((asset) => asset.uri));
      console.log("Images:", images);
    } else {
      // Handle the case when the user cancels image selection
      console.log("Image selection canceled");
    }

    if (!result.cancelled && result.assets[0].fileSize > MAX_FILE_SIZE) {
      alert("File size is too large. Please select a smaller file.");
      return;
    }
  };

  return (
    <View style={styles.container}>
      {images.map((uri, index) => (
        <Image key={index} source={{ uri }} style={styles.imagePreview} />
      ))}
      <TouchableOpacity
        style={styles.imagePickerButton}
        onPress={handleImagePick}
      >
        <MaterialIcons name="add-box" size={24} color="black" />
        <Text style={styles.buttonText}>CREATE POST</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  imagePickerButton: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#32A1A8",
    borderRadius: 5,
    paddingVertical: 10, // Adjust vertical padding as needed
    paddingHorizontal: 20, // Adjust horizontal padding as needed
  },
  buttonContainer: {
    backgroundColor: "black",
    padding: 10,
    width: 250,
    marginBottom: 20,
  },
  imagePreview: {
    width: 200,
    height: 200,
    marginVertical: 10,
    marginLeft: 30,
  },
  buttonText: {
    fontWeight: "bold",
    color: "white",
    textAlign: "center",
  },
  imageArea: {
    width: 200, // Set the width to an appropriate value
    height: 200, // Set the height to an appropriate value
    resizeMode: "cover", // Adjust the resizeMode property as needed
  },
});

export default selectImage;
