import React, { useContext, useState, useEffect } from "react";
import { View, StyleSheet, Image, ScrollView } from "react-native";
import Menu from "../Component/Menu";
import headlogo from "../../assets/download.png";
import header from "../../assets/header.png";
import { PostContext } from "../context/postContext";
import AdsCards from "../Component/Banner/AdsCards";
import axios from "axios";

const MyAdsPage = () => {
  //STATE
  const [posts, setPosts] = useState([]);

  const getUserPosts = async () => {
    try {
      const data = await API.get("/post/car/get-user-post");
      setPosts(data?.userPosts);
    } catch (error) {
      console.log(error);
      alert(error);
    }
  };
  //initial
  useEffect(() => {
    getUserPosts();
  }, []);
  return (
    <View style={styles.container}>
      <View style={styles.topContainer}>
        <Image source={headlogo} style={styles.headLogo} resizeMode="contain" />
        <View style={styles.headerContainer}>
          <Image
            source={header}
            style={styles.headerImage}
            resizeMode="contain"
          />
        </View>
      </View>
      <ScrollView style={styles.scrollContainer}>
        <AdsCards posts={posts} />
      </ScrollView>
      <View style={styles.bottomMenu}>
        <Menu />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  topContainer: {
    flexDirection: "row",
    alignItems: "center",
    position: "absolute",
    top: -32,
    left: -9,
  },
  headLogo: {
    width: 200,
    height: 200,
    margin: 10,
  },
  headerContainer: {
    justifyContent: "center",
    alignItems: "center",
    right: 50,
  },
  headerImage: {
    width: 300, // Adjust the width of your header image
    height: 70, // Adjust the height of your header image
    left: -110, // Center the header image
  },
  searchContainer: {
    position: "absolute",
    top: 100, // Adjust the top value to position the SearchBar below the headerImage
    width: "100%", // Set the width to occupy the full screen width
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
  },
  bottomMenu: {
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
    borderTopWidth: 2,
    borderTopColor: "#DDDDDD",
  },
  searchStyle: {
    fontSize: 30,
  },
  innerContainer: {
    height: 294,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25, // Updated: Removed quotes around the value
    marginBottom: 20,
  },
  innerContainerBox: {
    width: 302,
    height: 71,
    backgroundColor: "#FFFFFF",
    borderRadius: 25, // Updated: Removed quotes around the value
    marginTop: 25,
    marginLeft: 20,
  },

  innerContainerText: {
    color: "black",
    fontWeight: "bold",
    textAlign: "center",
  },
  scrollContainer: {
    height: 294,
    marginTop: 250,
  },
  cardContainer: {
    alignItems: "center",
    justifyContent: "center",
    width,
  },
  card: {
    width: width * 0.99,
    height: width * 0.5,
    resizeMode: "cover",
    borderRadius: 8,
    overflow: "hidden",
  },
  sliderContainer: {
    backgroundColor: "#D9D9D9", // Set the background color here for the Carousel
    alignItems: "center",
    justifyContent: "center",
    marginTop: 10,
  },
  titleHeading: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  innerTextContainer: {
    width: 302,
    height: 71,
    backgroundColor: "white",
    borderRadius: 25,
    left: 18,
  },
  titleContainer: {
    flexDirection: "row",
    left: 15,
    top: 10,
    fontWeight: "bold",
  },
  titlerentHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  postDetailsContainer: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "flex-end",
  },
  postDetailsText: {
    textAlign: "right",
    top: 25,
    right: 20,
  },
});

export default MyAdsPage;
