import React, { useContext } from "react";
import SearchBar from "../Component/Home/SearchBar";
import carlogo from "../../assets/carlogo.png";
import { View, Text, StyleSheet, Image, ScrollView } from "react-native";
import Menu from "../Component/Menu";
import AdsBanner from "../Component/Banner/AdsBanner";

import { PostContext } from "../../context/postContext";

const data = [
  { id: "1", text: "My FORD Model 2019" },
  { id: "2", text: "My FORD Model 2019" },
];

const CarPage = () => {
  const [posts] = useContext(PostContext);
  return (
    <View style={styles.container}>
      <View style={styles.topContainer}>
        <Image source={headlogo} style={styles.headLogo} resizeMode="contain" />
        <View style={styles.headerContainer}>
          <Image
            source={header}
            style={styles.headerImage}
            resizeMode="contain"
          />
        </View>
      </View>
      <View style={styles.searchContainer}>
        <SearchBar
          style={styles.searchStyle}
          setSearchText={(value) => console.log(value)}
          imageSource={carlogo}
          placeholder="SEARCH YOUR CAR"
        />
      </View>
      <ScrollView style={styles.scrollContainer}>
        <View style={styles.innerContainer}>
          <View style={styles.sliderContainer}>
            <AdsBanner />
          </View>
          {posts?.map((post, i) => (
            <View>
              <Text style={styles.postTitle}>Title: {post?.title}</Text>
              <Text>Make: {post?.make}</Text>
              <Text>Model: {post?.model}</Text>
              <Text>Variant: {post?.variant}</Text>
              <Text>Rent: {post?.rent}</Text>
              <Text>Description: {post?.description}</Text>
              <View style={styles.postDetails}>
                ,<Text>PostedBy: {post?.postedBy?.name}</Text>
                <Text>
                  CreatedAt: {moment(post?.createdAt).format("DD:MM:YYYY")}
                </Text>
              </View>
            </View>
          ))}
        </View>
      </ScrollView>
      <View style={styles.bottomMenu}>
        <Menu />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  topContainer: {
    flexDirection: "row",
    alignItems: "center",
    position: "absolute",
    top: -32,
    left: -9,
  },
  headLogo: {
    width: 200,
    height: 200,
    margin: 10,
  },
  headerContainer: {
    justifyContent: "center",
    alignItems: "center",
    right: 50,
  },
  headerImage: {
    width: 300, // Adjust the width of your header image
    height: 70, // Adjust the height of your header image
    left: -110, // Center the header image
  },
  searchContainer: {
    position: "absolute",
    top: 100, // Adjust the top value to position the SearchBar below the headerImage
    width: "100%", // Set the width to occupy the full screen width
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
  },
  bottomMenu: {
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
    borderTopWidth: 2,
    borderTopColor: "#DDDDDD",
  },
  searchStyle: {
    fontSize: 30,
  },
  innerContainer: {
    height: 294,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: "25px",
    marginBottom: 20,
  },
  innerContainerBox: {
    width: 302,
    height: 71,
    backgroundColor: "#FFFFFF",
    borderRadius: "25px",
    marginTop: 25,
    marginLeft: 20,
  },
  innerContainerText: {
    color: "black",
    fontWeight: "bold",
    textAlign: "center",
  },
  scrollContainer: {
    height: 294,
    marginTop: 250,
  },
});

export default CarPage;
