import React from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import SellerButtons from "../Component/Home/SellerButtons";
import carlogo from "../../assets/carlogo.png";
import apartlogo from "../../assets/apartlogo.png";
import applogo from "../../assets/applogo.png";
import furnlogo from "../../assets/furnlogo.png";

const SellerPage = ({ navigation }) => {
  const backButton = async () => {
    navigation.navigate("Dashboard");
  };
  const carInputs = [
    { key: "title", placeholder: "Title" },
    { key: "make", placeholder: "Make" },
    { key: "model", placeholder: "Model" },
    { key: "variant", placeholder: "Variant" },
    { key: "rent", placeholder: "Rent" },
    { key: "description", placeholder: "Description" },
  ];
  const carRequiredFields = ["title", "make", "model"]; // Specify required fields for car button

  const apartmentInputs = [
    { key: "title", placeholder: "Title" },
    { key: "flatNo", placeholder: "Flat No" },
    { key: "size", placeholder: "Size" },
    { key: "rooms", placeholder: "Rooms" },
    { key: "rent", placeholder: "Rent" },
    { key: "description", placeholder: "Description" },
  ];
  const apparelsInputs = [
    { key: "title", placeholder: "Title" },
    { key: "type", placeholder: "Clothes Type" },
    { key: "age", placeholder: "For Age" },
    { key: "fabric", placeholder: "Fabric" },
    { key: "rent", placeholder: "Rent" },
    { key: "description", placeholder: "Description" },
  ];
  const furnitureInputs = [
    { key: "title", placeholder: "Title" },
    { key: "name", placeholder: "Furniture Name" },
    { key: "color", placeholder: "Color" },
    { key: "quantity", placeholder: "Quantity" },
    { key: "rent", placeholder: "Rent" },
    { key: "description", placeholder: "Description" },
  ];
  return (
    <View style={styles.bgContainer}>
      <View style={styles.headerContainer}>
        <TouchableOpacity onPress={() => backButton()}>
          <Ionicons name="arrow-back-circle-sharp" size={24} color="black" />
        </TouchableOpacity>
        <Text style={styles.headerText}>WHAT ARE YOU OFFERING?</Text>
      </View>
      <View>
        <SellerButtons
          setbtnText={(value) => console.log(value)}
          imageSource={carlogo}
          placeholder="ADD YOUR CAR"
          inputs={carInputs}
          requiredFields={carRequiredFields}
        />
        <SellerButtons
          setbtnText={(value) => console.log(value)}
          imageSource={applogo}
          placeholder="ADD YOUR APPARELS"
          inputs={apparelsInputs}
        />
        <SellerButtons
          setbtnText={(value) => console.log(value)}
          imageSource={apartlogo}
          placeholder="ADD YOUR APPARTMENT"
          inputs={apartmentInputs}
        />
        <SellerButtons
          setbtnText={(value) => console.log(value)}
          imageSource={furnlogo}
          placeholder="ADD YOUR FURNITURE"
          inputs={furnitureInputs}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  bgContainer: {
    flex: 1,
    backgroundColor: "#D2C6C6",
  },
  headerContainer: {
    flexDirection: "row",
    backgroundColor: "#8A7A7A",
    height: 52,
    alignItems: "center",
    shadowColor: "#000000",
    shadowOffset: {
      width: 0,
      height: 3,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    paddingHorizontal: 10,
    marginBottom: 130,
  },
  headerText: {
    color: "#FFFFFF",
    fontSize: 15,
    textAlign: "center",
    marginLeft: 50,
  },
  btnContainer: {
    justifyContent: "center",
    alignItems: "center",
    marginTop: 20,
  },
});

export default SellerPage;
