import React from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Alert,
} from "react-native";
import Background from "../Background";
import Menu from "../Menu";
import {
  MaterialIcons,
  MaterialCommunityIcons,
  Ionicons,
} from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import { API } from "../../api/config";

const PostDetailScreen = ({ route }) => {
  const { post, myPostScreen, userId } = route.params;
  // console.log("userID from postDetail", userId);
  const navigation = useNavigation();

  const handleBackButton = () => {
    navigation.navigate("Dashboard");
  };
  const handleSubmit = () => {
    navigation.navigate("PaymentPage");
  };
  //handle delete Prompt
  //   const handlePrompt = (id) => {
  //     Alert.alert("ALERT!", "DO YOU REALLY WANT TO DELETE THIS POST?", [
  //       {
  //         text: "Cancel",
  //         onPress: () => {
  //           console.log("CANCEL PRESS");
  //         },
  //       },
  //       {
  //         text: "DELETE",
  //         onPress: () => handleDeletePost(id),

  //       },
  //     ]);
  //   };
  //delete post
  const handleDeletePost = async (id) => {
    try {
      const response = await API.delete(`/post/delete-post/${id}`);

      if (response.data.success) {
        alert(response.data.message);
        navigation.navigate("Dashboard", { userId: userId });
      } else {
        console.error(response.data.message);
        alert("Error deleting post");
      }
    } catch (error) {
      console.error(error);
      alert("Error deleting post");
    }
  };
  return (
    <View>
      <Background />
      <Ionicons
        name="arrow-back-sharp"
        size={30}
        color="white"
        style={{ top: -200, left: 12 }}
        onPress={() => handleBackButton()}
      />
      <View style={styles.headerStyle}>
        <Text style={styles.headerText}>SUBSCRIBE YOUR CAR</Text>
      </View>
      <View style={styles.outerContainer}>
        <View style={styles.outerImageContainer}>
          <ScrollView horizontal>
            {post?.postImages.map((image, index) => (
              <Image
                key={index}
                source={{ uri: image.url }}
                resizeMode="cover"
                style={styles.imageContainer}
              />
            ))}
          </ScrollView>
        </View>
        <View style={styles.innerMainContainer}>
          <View style={styles.innerContainer}>
            <View style={styles.upperheaderContainer}>
              <Text style={styles.upperTextContainer}>{post.name}</Text>
              <Text style={styles.upperTextContainer}>
                {post.room ? ` Room: ${post.room}` : ` Model: ${post.model}`}
              </Text>
              <Text style={styles.titlerentHeading}> | Rent: {post?.rent}</Text>
            </View>
            <View style={styles.lowerTextHeading}>
              <Text style={styles.lowertitleHeading}>
                {post.room ? `Area: ${post.area}` : `Make: ${post.make}`}
              </Text>
              <Text style={[styles.lowertitleHeading, { marginLeft: 25 }]}>
                {post.room
                  ? `Floor: ${post.floor}`
                  : `Variant: ${post.variant}`}
              </Text>
            </View>
          </View>
          <View style={styles.descriptionContainer}>
            <Text style={styles.discTextContainer}>Description:</Text>
            <Text style={{ left: 15 }}>{post?.description}</Text>
          </View>
          <View style={styles.sellerContainer}>
            {myPostScreen && (
              <View
                style={{
                  flexDirection: "row",
                  alignItems: "center",
                  left: 55,
                  top: 10,
                  gap: 150,
                }}
              >
                <MaterialCommunityIcons
                  name="pencil-plus"
                  size={24}
                  color="black"
                />
                <TouchableOpacity onPress={() => handleDeletePost(post?._id)}>
                  <MaterialIcons name="delete" size={24} color="black" />
                </TouchableOpacity>
              </View>
            )}
            {post?.postedBy?.name && (
              <Text style={[styles.upperTextContainer, { left: 15 }]}>
                Seller Name: {post?.postedBy?.name}
              </Text>
            )}
          </View>
          {!myPostScreen && userId !== post?.postedBy?._id && (
            // <StripeProvider publishableKey="pk_test_51OBalnCqGjyjTkAY9dTa4EdIxHfyluvV2pJtbExYurNHYgerZ0v3wnM4kz97bbIfgQ55YRbGPAqpxphvx0K6R2AC00CdB5YbIX">
            <TouchableOpacity
              style={styles.subscribeButton}
              onPress={() => handleSubmit()}
            >
              <MaterialIcons
                name="add-circle-outline"
                size={24}
                color="white"
              />
              <Text style={styles.subscribeButtonText}>SUBSCRIBE</Text>
            </TouchableOpacity>
            // </StripeProvider>
          )}
          {userId === post?.postedBy?._id && (
            <Text style={styles.ownPostText}>THIS IS YOUR OWN POST</Text>
          )}
        </View>
      </View>
      <Menu />
    </View>
  );
};

const styles = StyleSheet.create({
  outerContainer: {
    height: 500,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25,
    marginTop: -90,
    marginLeft: 25,
    marginBottom: 43,
  },
  imageContainer: {
    backgroundColor: "#D9D9D9",
    width: 340,
    height: 200,
    borderRadius: 25,
  },
  outerImageContainer: {
    bottom: 20,
  },
  headerStyle: {
    backgroundColor: "#D8D6F7",
    borderRadius: 26,
    width: 375,
    height: 81,
    justifyContent: "center",
    alignItems: "center",
    top: -130,
    left: 8,
  },
  headerText: {
    fontFamily: "appfont",
    fontSize: 20,
    color: "white",
    textShadowColor: "rgba(0, 0, 0, 0.75)",
    textShadowOffset: { width: -1, height: 1 },
    textShadowRadius: 10,
  },
  innerMainContainer: {
    alignItems: "center",
  },
  ownPostText: {
    fontSize: 15,
    color: "red",
    fontFamily: "appfont",
    textAlign: "center",
    marginTop: 20,
  },
  innerContainer: {
    top: 10,
    height: 87,
    width: 302,
    backgroundColor: "#FFFFFF",
    borderRadius: 25,
  },
  upperheaderContainer: {
    flexDirection: "row",
    left: 15,
    top: 10,
  },
  upperTextContainer: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  titlerentHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  lowerTextHeading: {
    flexDirection: "row",
    left: 15,
    top: 10,
  },
  lowertitleHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  descriptionContainer: {
    top: 25,
    width: 302,
    height: 58,
    backgroundColor: "#FFFFFF",
    borderRadius: 25,
  },
  discTextContainer: {
    fontSize: 18,
    fontFamily: "appfont",
    left: 15,
  },
  sellerContainer: {
    top: 40,
    width: 302,
    height: 58,
    backgroundColor: "#FFFFFF",
    borderRadius: 25,
  },
  subscribeButton: {
    display: "flex",
    flexDirection: "row",
    gap: 5,
    alignItems: "center",
    justifyContent: "center", // Center the content horizontally
    top: 50,
    width: 304,
    height: 35,
    backgroundColor: "#F41111",
    borderRadius: 25,
  },
  subscribeButtonText: {
    fontSize: 20,
    color: "#FFFFFF",
    fontFamily: "appfont",
  },
});

export default PostDetailScreen;
