import React from "react";
import {
  StyleSheet,
  View,
  Text,
  Image,
  Dimensions,
  Pressable,
} from "react-native";
import Carousel, { PaginationLight } from "react-native-x-carousel";
import { BannerData } from "../Data/BannerData";

const { width } = Dimensions.get("window");
const innerContainerWidth = 338; // Width of your inner container
const innerContainerHeight = 294; // Height of your inner container

const AdsBanner = ({ postImages }) => {
  const renderItem = (imageUri) => (
    <View style={styles.cardContainer}>
      <Image style={styles.card} source={{ uri: imageUri }} />
    </View>
  );

  return (
    <View style={[styles.container, styles.innerContainer]}>
      <View style={styles.innerCarousel}>
        <Carousel
          pagination={PaginationLight}
          renderItem={renderItem}
          data={postImages}
          loop
        />
      </View>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#D9D9D9",
  },
  innerContainer: {
    width: innerContainerWidth,
    height: innerContainerHeight,
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 25, // Apply the border radius here to match your inner container
    overflow: "hidden",
  },
  innerCarousel: {
    backgroundColor: "#D9D9D9", // Set the background color here for the Carousel
    alignItems: "center",
    justifyContent: "center",
    marginTop: 10,
  },
  cardContainer: {
    alignItems: "center",
    justifyContent: "center",
    width,
  },
  cardWrapper: {
    borderRadius: 8,
    overflow: "hidden",
  },
  card: {
    width: 302,
    height: 161,
  },
  cornerLabel: {
    position: "absolute",
    bottom: 0,
    right: 0,
    borderTopLeftRadius: 8,
  },
  cornerLabelText: {
    fontSize: 12,
    color: "#fff",
    fontWeight: "600",
    paddingLeft: 5,
    paddingRight: 5,
    paddingTop: 2,
    paddingBottom: 2,
  },
});

export default AdsBanner;
