import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  Button,
  Image,
  ScrollView,
} from "react-native";

const CarAdPage = () => {
  const [title, setTitle] = useState("");
  const [make, setMake] = useState("");
  const [model, setModel] = useState("");
  const [variant, setVariant] = useState("");
  const [rent, setRent] = useState("");
  const [description, setDescription] = useState("");
  const [images, setImages] = useState([]);

  const handleImageUpload = () => {
    // Implement image upload logic here (using ImagePicker or other libraries)
    // Update the images state with the selected images
  };

  const handleSubmit = () => {
    // Implement logic to handle the form submission (e.g., sending data to server/database)
    // Include fields: title, make, model, variant, rent, description, and images
  };

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.label}>Title:</Text>
      <TextInput
        style={styles.input}
        onChangeText={(text) => setTitle(text)}
        value={title}
        placeholder="Enter title"
      />
      {/* Similar TextInput components for other fields: make, model, variant, rent, description */}

      {/* Image upload section */}
      <Text style={styles.label}>Upload Images:</Text>
      <Button title="Choose Images" onPress={handleImageUpload} />

      {/* Display selected images */}
      <ScrollView horizontal>
        {images.map((image, index) => (
          <Image key={index} source={{ uri: image }} style={styles.image} />
        ))}
      </ScrollView>

      {/* Submit button */}
      <Button title="Submit Ad" onPress={handleSubmit} />
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  label: {
    fontSize: 18,
    fontWeight: "bold",
    marginBottom: 5,
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginBottom: 15,
    paddingHorizontal: 10,
  },
  image: {
    width: 100,
    height: 100,
    marginRight: 10,
  },
});

export default CarAdPage;
