import React from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import Background from "../Background";
import Menu from "../Menu";

const PostDetailScreen = ({ route }) => {
  const { post } = route.params;
  const handleSubmit = ({ navigation }) => {
    navigation.navigate("PaymentPage");
  };

  return (
    <View>
      <Background />
      <View style={styles.headerStyle}>
        <Text style={styles.headerText}>SUSBSCRIBE YOUR CAR</Text>
      </View>
      <View style={styles.outerContainer}>
        <View style={styles.outerImageContainer}>
          <ScrollView horizontal>
            {post?.postImages.map((image, index) => (
              <Image
                key={index}
                source={{ uri: image.url }}
                resizeMode="cover"
                style={styles.imageContainer}
              />
            ))}
          </ScrollView>
        </View>
        <View style={styles.innerContainer}>
          <Text>Title: {post.title}</Text>
          <Text>Model: {post.model}</Text>
          <Text>Make: {post?.make}</Text>
          <Text>Variant: {post?.variant}</Text>
        </View>
        <View style={styles.descriptionContainer}>
          <Text>Description: {post?.description}</Text>
        </View>
        <View style={styles.sellerContainer}>
          <Text>PostedBy: {post?.postedBy?.name}</Text>
        </View>
        <TouchableOpacity
          style={styles.subscribeButton}
          onPress={() => handleSubmit()}
        >
          <Text style={styles.subscribeButtonText}>SUSBSCRIBE</Text>
        </TouchableOpacity>
      </View>
      <Menu />
    </View>
  );
};

const styles = StyleSheet.create({
  outerContainer: {
    height: 500,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25,
    marginTop: -70,
    marginLeft: 25,
    marginBottom: 56,
    alignItems: "center",
  },
  imageContainer: {
    backgroundColor: "#D9D9D9",
    width: 340,
    height: 200,
    borderRadius: 25,
  },
  outerImageContainer: {
    bottom: 20,
  },
  headerStyle: {
    backgroundColor: "#D8D6F7",
    display: "flex",
    flexDirection: "row",
    gap: 5,
    alignItems: "center",
    borderRadius: 26,
    width: 375,
    height: 81,
    left: 8,
    top: -100,
  },
  headerText: {
    left: 80,
    fontFamily: "appfont",
    fontSize: 20,
    color: "black",
    textShadowColor: "rgba(0, 0, 0, 0.75)",
    textShadowOffset: { width: -1, height: 1 },
    textShadowRadius: 10,
  },
  innerContainer: {
    top: 10,
    height: 87,
    width: 302,
    backgroundColor: "#FFFFFF",
    borderRadius: 25,
  },
  descriptionContainer: {
    top: 25,
    width: 302,
    height: 58,
    backgroundColor: "#FFFFFF",
    borderRadius: 25,
  },
  sellerContainer: {
    top: 40,
    width: 302,
    height: 58,
    backgroundColor: "#FFFFFF",
    borderRadius: 25,
  },
  subscribeButton: {
    alignItems: "center",
    width: 304,
    height: 27,
    backgroundColor: "#F41111",
  },
  subscribeButtonText: {
    color: "#FFFFFF",
    fontFamily: "appfont",
  },
});

export default PostDetailScreen;
