import React from "react";
import {
  StyleSheet,
  View,
  Dimensions,
  ScrollView,
  Text,
  Image,
  TouchableOpacity,
} from "react-native";
import moment from "moment";
import { Ionicons } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";

const { width } = Dimensions.get("window");

const AdsCards = ({ posts, myPostScreen, userId }) => {
  const navigation = useNavigation();

  const handlePostPress = (post, myPostScreen) => {
    navigation.navigate("PostDetailScreen", { post, myPostScreen, userId });
  };
  return (
    <View>
      <View style={styles.container}>
        {posts?.map((post, index) => (
          <TouchableOpacity
            key={index}
            onPress={() => handlePostPress(post, myPostScreen)}
          >
            <View style={styles.innerContainer} key={index}>
              <ScrollView
                horizontal
                showsHorizontalScrollIndicator={false}
                pagingEnabled
              >
                {post?.postImages.map((image, imageIndex) => (
                  <Image
                    key={imageIndex}
                    source={{ uri: image.url }}
                    style={styles.card}
                    resizeMode="cover"
                    onError={(e) =>
                      console.log("Error loading image:", e.nativeEvent.error)
                    }
                  />
                ))}
              </ScrollView>
              <View style={styles.innerTextContainer}>
                <View style={styles.titleContainer}>
                  <Text style={styles.titleHeading}>{post?.title}</Text>
                  <Text style={styles.titleHeading}> Model {post?.model}</Text>
                  <Text style={styles.titlerentHeading}>
                    {" "}
                    | Rent: {post?.rent}
                  </Text>
                </View>
                {/* <Text>Make: {post?.make}</Text> */}
                {/* <Text>Variant: {post?.variant}</Text>
                <Text>Description: {post?.description}</Text> */}
                <View style={styles.postDetailsContainer}>
                  {/* <Text>PostedBy: {post?.postedBy?.name}</Text> */}
                  <View style={styles.ratingContainer}>
                    <Ionicons name="star" size={24} color="black" />
                    <Ionicons name="star" size={24} color="black" />
                    <Ionicons name="star" size={24} color="black" />
                    <Ionicons name="star" size={24} color="black" />
                    <Ionicons name="star-outline" size={24} color="black" />
                  </View>
                  <Text style={styles.postDetailsText}>
                    {moment(post?.createdAt).format("DD:MM:YYYY")}
                  </Text>
                </View>
              </View>
            </View>
          </TouchableOpacity>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    // flex: 1,
  },
  innerContainer: {
    height: 294,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25,
    marginBottom: 20,
    marginTop: 10,
  },
  card: {
    width: width * 0.99,
    height: width * 0.5,
    resizeMode: "cover",
    overflow: "hidden",
    borderRadius: 25,
  },
  innerTextContainer: {
    width: 302,
    height: 71,
    backgroundColor: "white",
    borderRadius: 25,
    left: 18,
    bottom: 15,
  },
  titleHeading: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  titleContainer: {
    flexDirection: "row",
    left: 15,
    top: 10,
    fontWeight: "bold",
  },
  titlerentHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  postDetailsContainer: {
    flex: 1,
    top: 25,
    flexDirection: "row",
  },
  postDetailsText: {
    textAlign: "right",
    right: -100,
  },
  ratingContainer: {
    flexDirection: "row",
    bottom: 10,
    left: 15,
  },
});

export default AdsCards;
