import React, { useState } from "react";
import { View, Text, TouchableOpacity, StyleSheet, Image } from "react-native";

const SellerButtons = ({ imageSource, placeholder }) => {
  return (
    <View>
      <TouchableOpacity onPress={() => setModalVisible(true)}>
        <View style={styles.searchbuttonStyle}>
          <View style={styles.buttonContent}>
            <View style={styles.circle}></View>
            <Image
              source={imageSource}
              style={styles.btnImageStyle}
              resizeMode="contain"
            />
            <Text style={styles.textStyle}>{placeholder}</Text>
          </View>
        </View>
      </TouchableOpacity>
    </View>
  );
};
const styles = StyleSheet.create({
  btnContainer: {
    alignItems: "center",
    marginTop: 20,
  },
  imagePreview: {
    width: 200,
    height: 200,
    marginVertical: 10,
    marginLeft: 30,
  },
  textStyle: {
    paddingHorizontal: 35,
    fontSize: 15,
    color: "black",
    fontWeight: "bold",
  },
  searchbuttonStyle: {
    backgroundColor: "#FFFFFF",
    flexDirection: "row",
    gap: 5,
    alignItems: "center",
    borderRadius: 26,
    width: 342,
    height: 81,
    marginTop: 40,
    marginLeft: 20,
  },
  buttonContent: {
    flexDirection: "row",
    alignItems: "center",
    flex: 1,
  },
  circle: {
    width: 70,
    height: 70,
    borderRadius: 35,
    backgroundColor: "#2F2E41",
    position: "absolute",
    top: -10,
    left: 10,
  },
  btnImageStyle: {
    width: 60,
    height: 50,
    left: 10,
    bottom: 0,
  },
  iconContainer: {
    flexDirection: "row",
    marginLeft: 10,
    marginRight: 10,
    alignItems: "center",
  },
  icon: {
    marginLeft: 10,
  },
  modalContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
  modalContent: {
    backgroundColor: "#fff",
    padding: 20,
    borderRadius: 20,
    width: 300,
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 2,
    marginVertical: 10,
    paddingHorizontal: 10,
  },

  descriptionInput: {
    height: 100, // Set the desired height for the description box
    borderColor: "gray",
    borderWidth: 2,
    marginVertical: 10,
    paddingHorizontal: 10,
  },
  saveButton: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#32A1A8",
    borderRadius: 5,
    paddingVertical: 10, // Adjust vertical padding as needed
    paddingHorizontal: 20, // Adjust horizontal padding as needed
  },

  buttonText: {
    marginLeft: 5, // Add left margin to create space between icon and text
    color: "#fff",
    fontWeight: "bold",
    fontSize: 18,
  },
  imagePickerButton: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DABFBF",
    borderRadius: 5,
    width: 260,
    height: 71,
    marginBottom: 10,
  },
  imagePickerText: {
    marginLeft: 5, // Add left margin to create space between icon and text
    fontWeight: "bold",
    fontSize: 18,
  },
  plusIcon: {
    marginLeft: 50, // Adjust marginLeft as needed to create the desired space
  },
});

export default SellerButtons;
