import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  Image,
  ScrollView,
} from "react-native";
import Background from "../Component/Background";
import { Ionicons } from "@expo/vector-icons";
import { MaterialIcons } from "@expo/vector-icons";
import { MaterialCommunityIcons } from "@expo/vector-icons";

const CarAdPage = () => {
  const [title, setTitle] = useState("");
  const [make, setMake] = useState("");
  const [model, setModel] = useState("");
  const [variant, setVariant] = useState("");
  const [rent, setRent] = useState("");
  const [description, setDescription] = useState("");
  const [images, setImages] = useState([]);

  const handleImageUpload = () => {
    // Implement image upload logic here (using ImagePicker or other libraries)
    // Update the images state with the selected images
  };

  const handleSubmit = () => {
    // Implement logic to handle the form submission (e.g., sending data to server/database)
    // Include fields: title, make, model, variant, rent, description, and images
  };

  return (
    <View>
      <Background />
      <ScrollView style={styles.container}>
        {/* Display selected images */}
        <ScrollView horizontal>
          {images.map((image, index) => (
            <Image key={index} source={{ uri: image }} style={styles.image} />
          ))}
        </ScrollView>
        {/* Image upload section */}
        <TouchableOpacity style={styles.button} onPress={handleImageUpload}>
          <Ionicons name="add-circle" size={24} color="black" />
          <Text style={styles.buttonText}> ADD IMAGE</Text>
        </TouchableOpacity>

        <Text style={styles.label}>TITLE:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setTitle(text)}
          value={title}
          placeholder="Enter Title"
        />
        <Text style={styles.label}>MAKE:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setTitle(text)}
          value={make}
          placeholder="Enter Make"
        />
        <Text style={styles.label}>MODEL:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setTitle(text)}
          value={model}
          placeholder="Enter Model"
        />
        <Text style={styles.label}>VARIANT:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setTitle(text)}
          value={variant}
          placeholder="Enter Variant"
        />
        <Text style={styles.label}>RENT:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setTitle(text)}
          value={rent}
          placeholder="Enter Rent"
        />
        <Text style={styles.label}>DESCRIPTION:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setTitle(text)}
          value={description}
          placeholder="Enter Description"
        />

        {/* Submit button */}
        <TouchableOpacity style={styles.saveButton} onPress={handleSubmit}>
          <MaterialIcons name="add-box" size={24} color="black" />
          <Text style={styles.buttonText}>CREATE POST</Text>
        </TouchableOpacity>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 20,
  },
  label: {
    fontSize: 18,
    fontWeight: "bold",
    marginBottom: 5,
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginBottom: 15,
    paddingHorizontal: 10,
  },
  image: {
    width: 100,
    height: 100,
    marginRight: 10,
  },
  saveButton: {
    flexDirection: "row",
    backgroundColor: "yellow",
  },
  button: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DABFBF",
    borderRadius: 5,
    width: 260,
    height: 71,
    marginBottom: 10,
  },
  buttonText: {
    marginLeft: 5, // Add left margin to create space between icon and text
    fontWeight: "bold",
    fontSize: 18,
  },
});

export default CarAdPage;
