import React, { useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  Modal,
  TextInput,
  StyleSheet,
  Image,
} from "react-native";

const SellerButtons = ({ setbtnText, imageSource, placeholder, inputs }) => {
  const [modalVisible, setModalVisible] = useState(false);
  const [values, setValues] = useState({});

  const handleSave = () => {
    // Do something with the user input, e.g., save it to state or send it to an API
    console.log("Values:", values);
    // Close the modal
    setModalVisible(false);
  };

  const renderInputs = () => {
    return inputs.map((input, index) => (
      <TextInput
        key={index}
        style={styles.input}
        placeholder={input.placeholder}
        onChangeText={(text) => setValues({ ...values, [input.key]: text })}
      />
    ));
  };

  return (
    <View>
      <TouchableOpacity onPress={() => setModalVisible(true)}>
        <View style={styles.searchbuttonStyle}>
          <View style={styles.buttonContent}>
            <View style={styles.circle}></View>
            <Image
              source={imageSource}
              style={styles.btnImageStyle}
              resizeMode="contain"
            />
            <Text style={styles.textStyle}>{placeholder}</Text>
          </View>
        </View>
        {/* For example, you can display an image and a placeholder */}
      </TouchableOpacity>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            {renderInputs()}
            <TouchableOpacity style={styles.saveButton} onPress={handleSave}>
              <Text style={styles.buttonText}>Save</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  btnContainer: {
    alignItems: "center",
    marginTop: 20,
  },
  textStyle: {
    paddingHorizontal: 35,
    fontSize: 15,
    color: "black",
    fontWeight: "bold",
  },
  searchbuttonStyle: {
    backgroundColor: "#FFFFFF",
    flexDirection: "row",
    gap: 5,
    alignItems: "center",
    borderRadius: 26,
    width: 342,
    height: 81,
    marginTop: 40,
    marginLeft: 20,
  },
  buttonContent: {
    flexDirection: "row",
    alignItems: "center",
    flex: 1,
  },
  circle: {
    width: 70,
    height: 70,
    borderRadius: 35,
    backgroundColor: "#2F2E41",
    position: "absolute",
    top: -10,
    left: 10,
  },
  btnImageStyle: {
    width: 60,
    height: 50,
    left: 10,
    bottom: 0,
  },
  iconContainer: {
    flexDirection: "row",
    marginLeft: 10,
    marginRight: 10,
    alignItems: "center",
  },
  icon: {
    marginLeft: 10,
  },
  modalContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
  modalContent: {
    backgroundColor: "#fff",
    padding: 20,
    borderRadius: 10,
    width: 300,
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginVertical: 10,
    paddingHorizontal: 10,
  },
  saveButton: {
    backgroundColor: "#4CAF50",
    borderRadius: 5,
    padding: 10,
    alignItems: "center",
  },
  buttonText: {
    color: "#fff",
    fontWeight: "bold",
  },
});

export default SellerButtons;
