import React, { useContext } from "react";
import { View, Text, StyleSheet, ScrollView, Dimensions } from "react-native";
import SearchBar from "../Component/Home/SearchBar";
import carlogo from "../../assets/carlogo.png";
import Menu from "../Component/Menu";
import headlogo from "../../assets/download.png";
import header from "../../assets/header.png";
import { PostContext } from "../context/postContext";
import AdsCards from "../Component/Banner/AdsCards";
import { AuthContext } from "../context/authContext";
import Background from "../Component/Background";

const { width } = Dimensions.get("window");
const innerContainerWidth = 338; // Width of your inner container
const innerContainerHeight = 294; // Height of your inner container

const CarPage = ({}) => {
  const [posts] = useContext(PostContext);
  const [userIdArray] = useContext(AuthContext); // Use AuthContext to get userId
  const userId = userIdArray?.data?.user?._id;

  // console.log("USER ID", userId);

  return (
    <View style={styles.container}>
      <Background />
      <View style={styles.searchContainer}>
        <SearchBar
          style={styles.searchStyle}
          setSearchText={(value) => console.log(value)}
          imageSource={carlogo}
          placeholder="SEARCH YOUR CAR"
        />
      </View>
      <Text style={styles.lengthText}>Total Posts: {posts?.length}</Text>
      {/* Display the userId */}
      <ScrollView style={styles.scrollContainer}>
        <AdsCards posts={posts} userId={userId} />
      </ScrollView>
      <View style={styles.bottomMenu}>
        <Menu />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },

  searchContainer: {
    position: "absolute",
    top: 120,
    width: "100%",
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
  },
  scrollContainer: {
    height: 294,
    marginTop: 50,
    bottom: 40,
    left: 25,
  },
  lengthText: {
    top: 10,
    left: 25,
  },
  bottomMenu: {
    position: "absolute",
    bottom: 20,
    left: 0,
    right: 0,
    borderTopWidth: 2,
    borderTopColor: "#DDDDDD",
  },
});

export default CarPage;
