import React, { createContext, useState, useEffect } from "react";
import { API } from "../api/config";

// CONTEXT
const PostContext = createContext();
const ApartmentPostContext = createContext();

// PROVIDERS
const PostProvider = ({ children }) => {
  const [posts, setPosts] = useState([]);

  const getAllPosts = async () => {
    try {
      const { data } = await API.get("/post/car/get-all-post");
      setPosts(data?.posts);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getAllPosts();
  }, []);

  return (
    <PostContext.Provider value={[posts, setPosts, getAllPosts]}>
      {children}
    </PostContext.Provider>
  );
};

const ApartmentPostProvider = ({ children }) => {
  const [posts, setPosts] = useState([]);

  const getAllPosts = async () => {
    try {
      const { data } = await API.get("/post/apartment/get-all-post");
      setPosts(data?.posts);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getAllPosts();
  }, []);

  return (
    <ApartmentPostContext.Provider value={[posts, setPosts, getAllPosts]}>
      {children}
    </ApartmentPostContext.Provider>
  );
};

export {
  PostContext,
  PostProvider,
  ApartmentPostContext,
  ApartmentPostProvider,
};
