import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  Image,
  ScrollView,
  Modal,
} from "react-native";
import { useDropzone } from "react-dropzone";
import { Ionicons } from "@expo/vector-icons";

const MAX_FILE_SIZE = 5 * 1024 * 1024; // 5 MB (in bytes)

const SelectImage = ({ onImagesSelected }) => {
  const [images, setImages] = useState([]);
  const [modalVisible, setModalVisible] = useState(false);

  const handleImageDrop = (acceptedFiles) => {
    // Close the modal
    setModalVisible(false);

    // Check file size
    if (acceptedFiles[0].size > MAX_FILE_SIZE) {
      alert("File size is too large. Please select a smaller file.");
      return;
    }

    // Update the images state variable with selected image URIs
    setImages(acceptedFiles.map((file) => URL.createObjectURL(file)));
    // Invoke the callback with selected image URIs
    onImagesSelected(acceptedFiles.map((file) => URL.createObjectURL(file)));
  };

  const { getRootProps, getInputProps } = useDropzone({
    accept: "image/*",
    onDrop: handleImageDrop,
  });

  return (
    <View style={styles.container}>
      <ScrollView horizontal>
        {images.map((uri, index) => (
          <Image key={index} source={{ uri }} style={styles.imagePreview} />
        ))}
      </ScrollView>

      <TouchableOpacity
        style={[styles.imagePickerButton, { backgroundColor: "#DABFBF" }]}
        onPress={() => setModalVisible(true)}
      >
        <Ionicons name="add-circle" size={24} color="black" />
        <Text style={styles.buttonText}>ADD IMAGE</Text>
      </TouchableOpacity>

      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalContainer}>
          <div {...getRootProps()} style={styles.modalButtonC}>
            <input {...getInputProps()} />
            <p>Drag 'n' drop some files here, or click to select files</p>
          </div>
          <TouchableOpacity
            style={styles.modalButtonC}
            onPress={() => setModalVisible(false)}
          >
            <Text style={styles.modalButtonText}>CANCEL</Text>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  imagePickerButton: {
    marginLeft: 5, // Add left margin to create space between icon and text
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DABFBF",
    borderRadius: 5,
    marginBottom: 5,
    paddingVertical: 10, // Adjust vertical padding as needed
    paddingHorizontal: 20, // Adjust horizontal padding as needed
  },
  imagePreview: {
    width: 200,
    height: 100,
    marginVertical: 10,
    marginLeft: 70,
  },
  buttonText: {
    marginLeft: 5, // Add left margin to create space between icon and text
    fontFamily: "appfont",
    fontSize: 18,
    color: "white",
  },
  imageArea: {
    width: 200, // Set the width to an appropriate value
    height: 200, // Set the height to an appropriate value
  },
  modalContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
  modalButton: {
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#DABFBF",
    marginVertical: 10,
    height: 50,
    width: 100,
    borderRadius: 5,
  },
  modalButtonC: {
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "row", // Arrange buttons horizontally
    backgroundColor: "#DABFBF",
    marginVertical: 10,
    height: 50,
    width: 200,
    borderRadius: 5,
  },

  modalButtonText: {
    fontFamily: "appfont",
    fontSize: 18,
    color: "white",
  },
});

export default SelectImage;
