import React from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Alert,
} from "react-native";
import Background from "../Background";
import Menu from "../Menu";
import { MaterialIcons, MaterialCommunityIcons } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import RNAlert from "react-native-alert";

const PostDetailScreen = ({ route }) => {
  const { post, myPostScreen } = route.params;
  const navigation = useNavigation();

  const handleSubmit = () => {
    navigation.navigate("PaymentPage");
  };
  //handle delete Prompt
  const handlePrompt = () => {
    RNAlert.alert(
      "ALERT!",
      "DO YOU REALLY WANT TO DELETE THIS POST?",
      [
        {
          text: "Cancel",
          onPress: () => {
            console.log("CANCEL PRESS");
          },
        },
        {
          text: "DELETE",
          onPress: () => console.log("HANDLE DELETE CALL"),
          // Add your delete logic here
        },
      ],
      { cancelable: false }
    );
  };

  return (
    <View>
      <Background />
      <View style={styles.headerStyle}>
        <Text style={styles.headerText}>SUSBSCRIBE YOUR CAR</Text>
      </View>
      <View style={styles.outerContainer}>
        <View style={styles.outerImageContainer}>
          <ScrollView horizontal>
            {post?.postImages.map((image, index) => (
              <Image
                key={index}
                source={{ uri: image.url }}
                resizeMode="cover"
                style={styles.imageContainer}
              />
            ))}
          </ScrollView>
        </View>
        <View style={styles.innerMainContainer}>
          <View style={styles.innerContainer}>
            <View style={styles.upperheaderContainer}>
              <Text style={styles.upperTextContainer}>{post.title}</Text>
              <Text style={styles.upperTextContainer}> Model {post.model}</Text>
              <Text style={styles.titlerentHeading}> | Rent: {post?.rent}</Text>
            </View>
            <View style={styles.lowerTextHeading}>
              <Text style={styles.lowertitleHeading}>Make: {post?.make}</Text>
              <Text style={[styles.lowertitleHeading, { marginLeft: 25 }]}>
                Variant: {post?.variant}
              </Text>
            </View>
          </View>
          <View style={styles.descriptionContainer}>
            <Text style={styles.discTextContainer}>Description:</Text>
            <Text style={{ left: 15 }}>{post?.description}</Text>
          </View>
          <View style={styles.sellerContainer}>
            {myPostScreen && (
              <View
                style={{
                  flexDirection: "row",
                  alignItems: "center",
                  left: 50,
                  top: 15,
                  gap: 150,
                }}
              >
                <MaterialCommunityIcons
                  name="pencil-plus"
                  size={24}
                  color="black"
                />
                <MaterialIcons
                  name="delete"
                  size={24}
                  color="black"
                  onPress={handlePrompt}
                />
              </View>
            )}
            {post?.postedBy?.name && (
              <Text style={[styles.upperTextContainer, { left: 15 }]}>
                Seller Name: {post?.postedBy?.name}
              </Text>
            )}
          </View>
          <TouchableOpacity
            style={styles.subscribeButton}
            onPress={() => handleSubmit()}
          >
            <MaterialIcons name="add-circle-outline" size={24} color="white" />
            <Text style={styles.subscribeButtonText}>SUSBSCRIBE</Text>
          </TouchableOpacity>
        </View>
      </View>
      <Menu />
    </View>
  );
};

const styles = StyleSheet.create({
  outerContainer: {
    height: 500,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25,
    marginTop: -70,
    marginLeft: 25,
    marginBottom: 56,
  },
  imageContainer: {
    backgroundColor: "#D9D9D9",
    width: 340,
    height: 200,
    borderRadius: 25,
  },
  outerImageContainer: {
    bottom: 20,
  },
  headerStyle: {
    backgroundColor: "#D8D6F7",
    display: "flex",
    flexDirection: "row",
    gap: 5,
    alignItems: "center",
    borderRadius: 26,
    width: 375,
    height: 81,
    left: 8,
    top: -100,
  },
  headerText: {
    left: 80,
    fontFamily: "appfont",
    fontSize: 20,
    color: "white",
    textShadowColor: "rgba(0, 0, 0, 0.75)",
    textShadowOffset: { width: -1, height: 1 },
    textShadowRadius: 10,
  },
  innerMainContainer: {
    alignItems: "center",
  },
  innerContainer: {
    top: 10,
    height: 87,
    width: 302,
    backgroundColor: "#FFFFFF",
    borderRadius: 25,
  },
  upperheaderContainer: {
    flexDirection: "row",
    left: 15,
    top: 10,
  },
  upperTextContainer: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  titlerentHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  lowerTextHeading: {
    flexDirection: "row",
    left: 15,
    top: 10,
  },
  lowertitleHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  descriptionContainer: {
    top: 25,
    width: 302,
    height: 58,
    backgroundColor: "#FFFFFF",
    borderRadius: 25,
  },
  discTextContainer: {
    fontSize: 18,
    fontFamily: "appfont",
    left: 15,
  },
  sellerContainer: {
    top: 40,
    width: 302,
    height: 58,
    backgroundColor: "#FFFFFF",
    borderRadius: 25,
  },
  subscribeButton: {
    display: "flex",
    flexDirection: "row",
    gap: 5,
    alignItems: "center",
    justifyContent: "center", // Center the content horizontally
    top: 50,
    width: 304,
    height: 35,
    backgroundColor: "#F41111",
    borderRadius: 25,
  },
  subscribeButtonText: {
    fontSize: 20,
    color: "#FFFFFF",
    fontFamily: "appfont",
  },
});

export default PostDetailScreen;
