import React, { useState } from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Alert,
  TextInput,
} from "react-native";
import Background from "../Background";
import Menu from "../Menu";
import {
  MaterialIcons,
  MaterialCommunityIcons,
  Ionicons,
  EvilIcons,
} from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import { API } from "../../api/config";

const PostDetailScreen = ({ route }) => {
  const { post, myPostScreen, userId } = route.params;
  // console.log("userID from postDetail", userId);
  const navigation = useNavigation();

  const handleBackButton = () => {
    navigation.navigate("Dashboard");
  };
  const handleSubmit = () => {
    navigation.navigate("PaymentPage");
  };
  useEffect(() => {
    if (post?.status === "rejected") {
      const deleteAlert = setTimeout(() => {
        Alert.alert(
          "Post Deletion Warning",
          "This post will be deleted in one day.",
          [
            {
              text: "OK",
              onPress: () => {
                // Handle the user acknowledgment if needed
              },
            },
          ]
        );
      }, 24 * 60 * 60 * 1000); // Show alert after one day

      // Clear the timeout when the component unmounts
      return () => clearTimeout(deleteAlert);
    }
  }, [post?.status]);
  //handle delete Prompt
  //   const handlePrompt = (id) => {
  //     Alert.alert("ALERT!", "DO YOU REALLY WANT TO DELETE THIS POST?", [
  //       {
  //         text: "Cancel",
  //         onPress: () => {
  //           console.log("CANCEL PRESS");
  //         },
  //       },
  //       {
  //         text: "DELETE",
  //         onPress: () => handleDeletePost(id),

  //       },
  //     ]);
  //   };
  //delete post
  const handleDeletePost = async (id) => {
    try {
      const token = API.defaults.headers.common["Authorization"];
      console.log(token);
      // Check if the token is present in the headers
      if (!token || token.trim() === "") {
        alert("Authorization token missing. Please log in.");
        // Redirect the user to the login screen or handle the absence of the token accordingly
        return;
      }
      const response = await API.delete(`/post/delete-post/${id}`, {
        headers: {
          Authorization: token,
        },
      });

      if (response.data.success) {
        alert(response.data.message);
        navigation.navigate("Dashboard", { userId: userId });
      } else {
        console.error(response.data.message);
        alert("Error deleting post");
      }
    } catch (error) {
      console.error(error);
      alert("Error deleting post");
    }
  };
  const [comment, setComment] = useState("");
  const [comments, setComments] = useState([]);

  const handleAddComment = () => {
    if (comment.trim() !== "") {
      setComments([...comments, comment]);
      setComment(""); // Clear the comment input
    }
  };
  return (
    <View>
      <Background />
      <Ionicons
        name="arrow-back-sharp"
        size={30}
        color="white"
        style={{ top: -200, left: 12 }}
        onPress={() => handleBackButton()}
      />
      <View style={styles.superContainer}>
        <View style={styles.headerStyle}>
          <Text style={styles.headerText}>SUBSCRIBE YOUR CAR</Text>
        </View>
        <View style={styles.overallContainer}>
          <View style={styles.outerContainer}>
            <View style={styles.outerImageContainer}>
              {post?.postImages && post.postImages.length > 0 ? (
                <ScrollView horizontal>
                  {post.postImages.map((image, index) => (
                    <Image
                      key={index}
                      source={{ uri: image.url }}
                      resizeMode="cover"
                      style={styles.imageContainer}
                    />
                  ))}
                </ScrollView>
              ) : (
                <View style={styles.imageContainer} />
              )}
            </View>

            <View style={styles.innerMainContainer}>
              <View style={styles.upperheaderContainer}>
                <Text style={styles.upperTextContainer}>{post.name}</Text>
                <Text style={styles.upperTextContainer}>
                  {post.room ? ` Room: ${post.room}` : ` Model: ${post.model}`}
                </Text>
                <Text style={styles.titlerentHeading}>
                  {" "}
                  | Rent: {post?.rent}
                </Text>
              </View>
              <View style={styles.lowerHeaderContainer}>
                <Text style={styles.lowertitleHeading}>
                  {post.room ? `Area: ${post.area}` : `Make: ${post.make}`}
                </Text>
                <Text style={[styles.lowertitleHeading, { marginLeft: 25 }]}>
                  {post.room
                    ? `Floor: ${post.floor} th`
                    : `Variant: ${post.variant}`}
                </Text>
              </View>
              <View style={styles.descriptionContainer}>
                <Text style={styles.discTextContainer}>Description:</Text>
                <Text>{post?.description}</Text>
              </View>
              {myPostScreen && (
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    justifyContent: "center",
                    top: 50,
                    gap: 150,
                  }}
                >
                  <MaterialCommunityIcons
                    name="pencil-plus"
                    size={24}
                    color="black"
                  />
                  <TouchableOpacity onPress={() => handleDeletePost(post?._id)}>
                    <MaterialIcons name="delete" size={24} color="black" />
                  </TouchableOpacity>
                </View>
              )}
              {post?.postedBy?.name && (
                <Text
                  style={[
                    styles.upperTextContainer,
                    { marginLeft: 10, top: 30 },
                  ]}
                >
                  Seller Name: {post?.postedBy?.name}
                </Text>
              )}
              <View style={styles.commentContainer}>
                <View style={styles.commentInputContainer}>
                  <TextInput
                    multiline
                    placeholder="Add a comment..."
                    style={styles.commentInput}
                    value={comment}
                    onChangeText={(text) => setComment(text)}
                  />
                  <EvilIcons
                    name="arrow-right"
                    size={25}
                    color="black"
                    style={styles.arrowIcon}
                    onPress={handleAddComment}
                  />
                </View>
              </View>
              <View style={styles.scrollviewContainer}>
                <ScrollView>
                  {comments.map((item, index) => (
                    <View key={index} style={styles.comment}>
                      <Text>{item}</Text>
                    </View>
                  ))}
                </ScrollView>
              </View>
              {!myPostScreen && userId !== post?.postedBy?._id && (
                <TouchableOpacity
                  style={styles.subscribeButton}
                  onPress={() => handleSubmit()}
                >
                  <MaterialIcons
                    name="add-circle-outline"
                    size={24}
                    color="white"
                  />
                  <Text style={styles.subscribeButtonText}>SUBSCRIBE</Text>
                </TouchableOpacity>
              )}
              {userId === post?.postedBy?._id && (
                <View>
                  <Text style={styles.ownPostText}>THIS IS YOUR OWN POST</Text>
                  <View
                    style={[
                      styles.statusStyle,
                      post?.status === "pending"
                        ? styles.pending
                        : post?.status === "approved"
                        ? styles.approved
                        : post?.status === "rejected"
                        ? styles.rejected
                        : {},
                    ]}
                  >
                    <Text>Post Status: {post?.status}</Text>
                  </View>
                </View>
              )}
            </View>
          </View>
        </View>
      </View>
      <View style={styles.bottomMenu}>
        <Menu />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  superContainer: {
    justifyContent: "center",
    alignItems: "center",
    // top: 90,
  },
  overallContainer: {
    justifyContent: "center",
    alignItems: "center",
    top: -190,
  },
  outerContainer: {
    top: 30,
    height: 550,
    width: 380,
    backgroundColor: "#D9D9D9",
    borderRadius: 25,
    marginBottom: 60,
  },
  innerMainContainer: {
    top: -40,
    width: 360,
    height: 340,
    backgroundColor: "#FFFFFF",
    borderRadius: 20,
    left: 10,
  },
  imageContainer: {
    backgroundColor: "#D9D9D9",
    width: 380,
    height: 200,
    borderRadius: 25,
  },
  outerImageContainer: {
    bottom: 50,
  },
  headerStyle: {
    backgroundColor: "#D8D6F7",
    borderRadius: 26,
    width: 375,
    height: 81,
    top: -30,
    justifyContent: "center",
    alignItems: "center",
  },
  headerText: {
    fontFamily: "appfont",
    fontSize: 20,
    color: "white",
    textShadowColor: "rgba(0, 0, 0, 0.75)",
    textShadowOffset: { width: -1, height: 1 },
    textShadowRadius: 10,
  },

  // innerContainer: {
  //   height: 87,
  //   width: 360,
  //   backgroundColor: "#FFFFFF",
  //   borderRadius: 25,
  // },
  upperheaderContainer: {
    top: 10,
    flexDirection: "row",
    textAlign: "left", // Align text to the left
    marginLeft: 10, // Add a left margin for consistent gap
  },
  lowerHeaderContainer: {
    top: 10,
    flexDirection: "row",
    textAlign: "left", // Align text to the left
    marginLeft: 10, // Add a left margin for consistent gap
  },
  descriptionContainer: {
    top: 20,
    textAlign: "left", // Align text to the left
    marginLeft: 10, // Add a left margin for consistent gap
  },
  titlerentHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  lowertitleHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  upperTextContainer: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  discTextContainer: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  ownPostText: {
    fontSize: 15,
    color: "red",
    fontFamily: "appfont",
    textAlign: "center",
    marginTop: 20,
  },
  // sellerContainer: {
  //   top: 20,
  //   width: 360,
  //   height: 35,
  //   backgroundColor: "#FFFFFF",
  //   borderRadius: 25,
  // },
  commentContainer: {
    top: 60,
    width: 340,
    backgroundColor: "#FFFFFF",
    borderWidth: 2,
    borderColor: "#DDDDDD",
    left: 10,
  },
  comment: {
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: "#DDDDDD",
  },
  commentInputContainer: {
    flexDirection: "row",
    alignItems: "center",
    paddingHorizontal: 10,
  },
  commentInput: {
    flex: 1,
    height: "100%", // Match the height of the container
  },
  scrollviewContainer: {
    top: 60,
    height: 100,
    width: 340,
    left: 10,
  },
  arrowIcon: {
    marginLeft: 10, // Adjust the spacing between input and arrow as needed
  },
  subscribeButton: {
    display: "flex",
    flexDirection: "row",
    gap: 5,
    alignItems: "center",
    justifyContent: "center", // Center the content horizontally
    top: 85,
    width: 360,
    height: 35,
    backgroundColor: "#F41111",
    borderRadius: 25,
  },
  subscribeButtonText: {
    fontSize: 20,
    color: "#FFFFFF",
    fontFamily: "appfont",
  },
  bottomMenu: {
    position: "absolute",
    // bottom: -100,
    bottom: 160,
    left: 0,
    right: 0,
    borderTopWidth: 2,
    borderTopColor: "#DDDDDD",
  },
  statusStyle: {
    alignItems: "center",
    justifyContent: "center",
    padding: 10,
    borderRadius: 15,
    marginBottom: 10,
    marginLeft: 10,
    marginRight: 10,
