import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  Alert,
  SafeAreaView,
  Image,
} from "react-native";
import React, { useState, useEffect, useContext } from "react";
import Checkbox from "expo-checkbox";
import {
  ClerkProvider,
  SignedIn,
  SignedOut,
  useSession,
} from "@clerk/clerk-expo";
import SignInWithOAuth from "./Component/SignInWithOAuth";
import Dashboard from "./Pages/Dashboard";
import LoginPage from "./LoginPage";
import axios from "axios";

const logo = require("../assets/logoW.png");
const headlogo = require("../assets/download.png");

const SignUp = ({ navigation }) => {
  const [email, setemail] = useState("");
  const [userName, setuserName] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setconfirmPassword] = useState("");
  const [agree, setAgree] = useState(false);

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePassword = (password) => {
    return password.length >= 6;
  };

  const submit = async () => {
    const isEmailValid = validateEmail(email);
    const isPasswordValid = validatePassword(password);
    const isPasswordMatch = password === confirmPassword;
    try {
      if (!userName || !email || !password) {
        Alert.alert("Please Fill All Fields!");
      } else {
        console.log("DATA=>>", userName, email, password);
        const data = await axios.post("/signUp", {
          userName,
          email,
          password,
        });
        console.log("Check", data);
        if (data && data.data) {
          alert(data);
          console.log("DATA=>>", userName, email, password);
          Alert.alert("You are Signed In!");
          navigation.navigate("LoginPage");
        } else {
          alert("data data is undefined");
        }
      }
    } catch (error) {
      alert(error.data ? error.data.data.message : "Unknown error occurred");
      console.log(error);
    }
  };
  return (
    <View style={styles.bgContainer}>
      <View style={styles.imageContainer}>
        <Image
          source={headlogo}
          style={styles.headStyle}
          resizeMode="contain"
        />
      </View>
      <View style={styles.headerContainer}></View>
      <Image source={logo} style={styles.logo} />
      <Text style={styles.header}>FLEX SHARE</Text>

      <Text style={styles.lowerheader}>LETâ€™S SUBSCRIBE</Text>
      <View style={styles.innerContainer}>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.inputStyle}
            placeholder="ENTER YOUR EMAIL"
            autoCapitalize="none"
            placeholderTextColor="white"
            autoCorrect={false}
            value={email}
            onChangeText={(actualData) => setemail(actualData)}
          />
        </View>

        <View style={styles.inputContainer}>
          <TextInput
            style={styles.inputStyle}
            placeholder="ENTER YOUR userName"
            autoCapitalize="none"
            placeholderTextColor="white"
            autoCorrect={false}
            value={userName}
            onChangeText={(actualData) => setuserName(actualData)}
          />
        </View>

        <View style={styles.inputContainer}>
          <TextInput
            style={styles.inputStyle}
            placeholder="ENTER YOUR PASSWORD"
            autoCapitalize="none"
            placeholderTextColor="white"
            autoCorrect={false}
            secureTextEntry={true}
            value={password}
            onChangeText={(actualData) => setPassword(actualData)}
          />
        </View>

        <View style={styles.inputContainer}>
          <TextInput
            style={styles.inputStyle}
            placeholder="CONFIRM YOUR PASSWORD"
            autoCapitalize="none"
            placeholderTextColor="white"
            autoCorrect={false}
            secureTextEntry={true}
            value={confirmPassword}
            onChangeText={(actualData) => setconfirmPassword(actualData)}
          />
        </View>

        <View style={styles.wrapper}>
          <Checkbox
            value={agree}
            onValueChange={() => setAgree(!agree)}
            color={agree ? "#4630EB" : undefined}
            borderRadius="1px"
            solid
          />
          <Text style={styles.wrapperText}>
            I AGREE TO TERMS AND CONDITIONS!
          </Text>
        </View>

        <TouchableOpacity
          style={[
            styles.buttonStyle,
            { backgroundColor: agree ? "#32A1A8" : "grey", marginTop: 20 },
          ]}
          disabled={!agree}
          onPress={() => submit()}
        >
          <Text style={styles.buttonText}>SIGN IN</Text>
        </TouchableOpacity>

        <ClerkProvider
          publishableKey={
            "pk_test_YXB0LXRlcnJhcGluLTkzLmNsZXJrLmFjY291bnRzLmRldiQ"
          }
        >
          <SafeAreaView style={{ flex: 1 }}>
            <SignedIn>
              <Dashboard />
            </SignedIn>
            <SignedOut>
              <SignInWithOAuth />
            </SignedOut>
          </SafeAreaView>
        </ClerkProvider>
        <View style={styles.lowerContainer}></View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  bgContainer: {
    flex: 1,
    backgroundColor: "#F2F2F2",
    alignItems: "center",
    justifyContent: "center",
  },
  imageContainer: {
    position: "absolute",
    bottom: "69%",
    left: 0,
  },

  /* innerContainer: {
      borderRadius: 10,
      padding: 20,
    },
  */
  headStyle: {
    width: 210,
    height: 300,
  },
  headerContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginBottom: 320, // Adjust the marginBottom for spacing
  },
  headerImageStyle: {
    width: "100%",
    height: "100%",
  },
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  header: {
    color: "rgba(0, 0, 0, 0.81)",
    fontSize: 24,
    fontStyle: "normal",
    fontWeight: "700",
    marginLeft: 10, // Add marginLeft for spacing between logo and text
  },
  lowerheader: {
    color: "rgba(0, 0, 0, 0.58)",
    fontSize: 14,
    fontStyle: "normal",
    fontWeight: "700",
    marginBottom: 20, // Adjust marginBottom for spacing
  },
  logo: {
    display: "flex",
    width: 110,
    height: 91,
    justifyContent: "center",
    alignItems: "center",
    flexShrink: 0,
  },
  mainContainer: {
    height: "100%",
  },
  innerContainer: {
    borderRadius: 10,
    padding: 20,
    marginTop: 40,
    marginBottom: -30,
  },
  mainHeader: {
    borderRadius: 10,
    paddingHorizontal: 100,
    justifyContent: "center",
    fontFamily: "serif",
    fontSize: 30,
    color: "white",
    fontWeight: "bold",
    marginBottom: 10,
  },
  inputContainer: {
    marginBottom: 10,
  },
  inputStyle: {
    borderColor: "rgba(160, 97, 106, 0.47)",
    backgroundColor: "rgba(160, 97, 106, 0.47)",
    paddingHorizontal: 30,
    paddingVertical: 5,
    borderRadius: 100,
    width: 340,
    height: 61,
    fontSize: 16,
  },
  buttonStyle: {
    marginTop: 20,
    backgroundColor: "#009688",
    borderRadius: 10,
    paddingVertical: 15,
    paddingHorizontal: 20,
  },
  buttonText: {
    fontSize: 18,
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },
  wrapper: {
    marginTop: 35,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 5,
  },
  wrapperText: {
    marginLeft: 10,
    fontSize: 14,
    color: "black",
    fontWeight: "bold",
  },
  signupText: {
    fontWeight: "bold",
    color: "#32A1A8",
    fontSize: 16,
    alignItems: "center",
    marginTop: 1,
  },
  lowerContainer: {
    fontSize: 16,
    fontWeight: "bold",
    flexDirection: "row",
    marginTop: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  lowerText: {
    fontSize: 16,
    fontWeight: "bold",
  },
});

export default SignUp;
