import React from "react";
import { StyleSheet, View, Image, Dimensions, Text } from "react-native";
import Carousel, { PaginationLight } from "react-native-x-carousel";
import moment from "moment";

const { width } = Dimensions.get("window");
const innerContainerWidth = 338; // Width of your inner container
const innerContainerHeight = 294; // Height of your inner container

const AdsCards = ({ posts }) => {
  const renderItem = ({ post }) => (
    <View style={styles.cardContainer} key={post?.postImages.url}>
      <Image
        source={{ uri: post?.postImages.url }}
        style={{ width: width, height: width * 0.5 }}
        resizeMode="cover"
      />
      {/* <Image source={{ uri: data }} style={styles.card} /> */}
    </View>
  );
  return (
    <View style={[styles.container, styles.innerContainer]}>
      <View>
        <Text>Total Posts: {posts?.length}</Text>
        {posts?.map((post, index) => (
          <View style={styles.innerContainer} key={index}>
            {" "}
            <View style={styles.sliderContainer}>
              <Carousel
                pagination={PaginationLight}
                renderItem={renderItem}
                data={post?.postImages}
                loop
                itemsPerPage={1}
                horizontal={true}
                sliderWidth={width}
                itemWidth={width}
              />
            </View>
            <View style={styles.innerTextContainer}>
              <View style={styles.titleContainer}>
                <Text style={styles.titleHeading}>{post?.title}</Text>
                <Text style={styles.titleHeading}> Model {post?.model}</Text>
                <Text style={styles.titlerentHeading}>
                  {" "}
                  | Rent: {post?.rent}
                </Text>
              </View>
              {/* <Text>Make: {post?.make}</Text> */}
              {/* <Text>Variant: {post?.variant}</Text>
                <Text>Description: {post?.description}</Text> */}
              <View style={styles.postDetailsContainer}>
                {/* <Text>PostedBy: {post?.postedBy?.name}</Text> */}
                <Text style={styles.postDetailsText}>
                  {moment(post?.createdAt).format("DD:MM:YYYY")}
                </Text>
              </View>
            </View>
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#D9D9D9",
  },
  innerContainer: {
    height: 294,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25, // Updated: Removed quotes around the value
    marginBottom: 20,
  },
  innerContainerBox: {
    width: 302,
    height: 71,
    backgroundColor: "#FFFFFF",
    borderRadius: 25, // Updated: Removed quotes around the value
    marginTop: 25,
    marginLeft: 20,
  },

  innerContainerText: {
    color: "black",
    fontWeight: "bold",
    textAlign: "center",
  },
  scrollContainer: {
    height: 294,
    marginTop: 250,
  },
  cardContainer: {
    alignItems: "center",
    justifyContent: "center",
    width,
  },
  card: {
    width: width * 0.99,
    height: width * 0.5,
    resizeMode: "cover",
    borderRadius: 8,
    overflow: "hidden",
  },
  sliderContainer: {
    backgroundColor: "#D9D9D9", // Set the background color here for the Carousel
    alignItems: "center",
    justifyContent: "center",
    marginTop: 10,
  },
  titleHeading: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  innerTextContainer: {
    width: 302,
    height: 71,
    backgroundColor: "white",
    borderRadius: 25,
    left: 18,
  },
  titleContainer: {
    flexDirection: "row",
    left: 15,
    top: 10,
    fontWeight: "bold",
  },
  titlerentHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  postDetailsContainer: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "flex-end",
  },
  postDetailsText: {
    textAlign: "right",
    top: 25,
    right: 20,
  },
});

export default AdsCards;
