import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Animated,
  TextInput,
  Picker,
} from "react-native";
import { Easing } from "react-native-reanimated";
import Background from "../Component/Background";

const PaymentPage = () => {
  const animatedValue = new Animated.Value(0);

  const [cnic, setCnic] = useState("");
  const [selectedCountry, setSelectedCountry] = useState("USA");
  const [expiryDate, setExpiryDate] = useState("");
  const [zipCode, setZipCode] = useState("");

  const animatePaymentOption = (index) => {
    Animated.timing(animatedValue, {
      toValue: index,
      duration: 300,
      easing: Easing.ease,
      useNativeDriver: false,
    }).start();
  };

  const paymentOptionStyle = (index) => {
    return {
      backgroundColor: animatedValue.interpolate({
        inputRange: [index - 1, index, index + 1],
        outputRange: ["#32A1A8", "#4CB8C4", "#32A1A8"],
      }),
    };
  };

  return (
    <View style={styles.container}>
      <Background />

      <View style={styles.contentContainer}>
        <Text style={styles.description}>CHECKOUT</Text>
        <Text style={styles.header}>PAYMENT METHOD</Text>

        {/* Additional input fields */}
        <Picker
          selectedValue={selectedCountry}
          onValueChange={(itemValue) => setSelectedCountry(itemValue)}
          style={styles.picker}
        >
          <Picker.Item label="Karachi" value="Karachi" />
          <Picker.Item label="Lahore" value="Lahore" />
          <Picker.Item label="Islamabad" value="Islamabad" />
          <Picker.Item label="Rawalpindi" value="Rawalpindi" />
          <Picker.Item label="Faisalabad" value="Faisalabad" />
          <Picker.Item label="Multan" value="Multan" />
          <Picker.Item label="Peshawar" value="Peshawar" />
          <Picker.Item label="Quetta" value="Quetta" />
          <Picker.Item label="Sialkot" value="Sialkot" />
          <Picker.Item label="Gujranwala" value="Gujranwala" />

          {/* Add more countries as needed */}
        </Picker>

        <TextInput
          style={styles.inputField}
          placeholder="CNIC Number"
          value={cnic}
          onChangeText={(text) => setCnic(text)}
        />

        <TextInput
          style={styles.inputField}
          placeholder="Expiry Date"
          value={expiryDate}
          onChangeText={(text) => setExpiryDate(text)}
        />

        <TextInput
          style={styles.inputField}
          placeholder="Zip Code"
          value={zipCode}
          onChangeText={(text) => setZipCode(text)}
        />

        {/* Done and Cross buttons */}
        <View style={styles.buttonsContainer}>
          <TouchableOpacity style={styles.paybutton}>
            <Text style={styles.buttonText}>PAY NOW</Text>
          </TouchableOpacity>
          {/* <TouchableOpacity style={styles.button}>
            <Text style={styles.buttonText}>Cross</Text>
          </TouchableOpacity> */}
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  contentContainer: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    paddingHorizontal: 20,
    marginTop: -50, // Move everything up a bit
  },
  header: {
    fontSize: 28,
    fontWeight: "bold",
    marginBottom: 20,
    color: "#333",
  },
  description: {
    fontSize: 28,
    fontWeight: "bold",
    marginBottom: 20,
    color: "#A19393",
  },
  inputField: {
    height: 40,
    backgroundColor: "#D1B4B8",
    borderWidth: 1,
    borderRadius: 8,
    width: 300,
    paddingHorizontal: 10,
    marginBottom: 15,
  },
  picker: {
    height: 40,
    width: 300,
    marginBottom: 15,
  },
  buttonsContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    width: 300,
  },
  paybutton: {
    marginTop: 20,
    backgroundColor: "#009688",
    borderRadius: 500,
    paddingVertical: 15,
    paddingHorizontal: 20,
  },
  buttonText: {
    color: "white",
    fontWeight: "bold",
  },
});

export default PaymentPage;
