import React from "react";
import { StyleSheet, View, Image, Dimensions, Text } from "react-native";
import Carousel, { PaginationLight } from "react-native-x-carousel";
import moment from "moment";

const { width } = Dimensions.get("window");
const innerContainerWidth = 338; // Width of your inner container
const innerContainerHeight = 294; // Height of your inner container

const AdsCards = ({ posts }) => {
  return (
    <View>
      <Text>Total Posts: {posts?.length}</Text>
      <View style={[styles.container, styles.innerContainer]}>
        {posts?.map((post, index) => (
          <View style={styles.innerContainer} key={index}>
            {" "}
            <View style={styles.sliderContainer}>
              {post?.postImages.map((image, imageIndex) => (
                <Carousel
                  pagination={PaginationLight}
                  key={imageIndex}
                  source={{ uri: image.url }}
                  loop
                  itemsPerPage={1}
                  horizontal={true}
                  sliderWidth={width}
                  itemWidth={width}
                  style={styles.card}
                  resizeMode="cover"
                />
              ))}
            </View>
            <View style={styles.innerTextContainer}>
              <View style={styles.titleContainer}>
                <Text style={styles.titleHeading}>{post?.title}</Text>
                <Text style={styles.titleHeading}> Model {post?.model}</Text>
                <Text style={styles.titlerentHeading}>
                  {" "}
                  | Rent: {post?.rent}
                </Text>
              </View>
              {/* <Text>Make: {post?.make}</Text> */}
              {/* <Text>Variant: {post?.variant}</Text>
                <Text>Description: {post?.description}</Text> */}
              <View style={styles.postDetailsContainer}>
                {/* <Text>PostedBy: {post?.postedBy?.name}</Text> */}
                <Text style={styles.postDetailsText}>
                  {moment(post?.createdAt).format("DD:MM:YYYY")}
                </Text>
              </View>
            </View>
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#D9D9D9",
  },
  innerContainer: {
    height: 294,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25, // Updated: Removed quotes around the value
    marginBottom: 20,
  },
  innerContainerBox: {
    width: 302,
    height: 71,
    backgroundColor: "#FFFFFF",
    borderRadius: 25, // Updated: Removed quotes around the value
    marginTop: 25,
    marginLeft: 20,
  },

  innerContainerText: {
    color: "black",
    fontWeight: "bold",
    textAlign: "center",
  },
  scrollContainer: {
    height: 294,
    marginTop: 250,
  },
  cardContainer: {
    alignItems: "center",
    justifyContent: "center",
    width,
  },
  card: {
    width: width * 0.99,
    height: width * 0.5,
    resizeMode: "cover",
    borderRadius: 8,
    overflow: "hidden",
  },
  sliderContainer: {
    backgroundColor: "#D9D9D9", // Set the background color here for the Carousel
    alignItems: "center",
    justifyContent: "center",
    marginTop: 10,
  },
  titleHeading: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  innerTextContainer: {
    width: 302,
    height: 71,
    backgroundColor: "white",
    borderRadius: 25,
    left: 18,
  },
  titleContainer: {
    flexDirection: "row",
    left: 15,
    top: 10,
    fontWeight: "bold",
  },
  titlerentHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  postDetailsContainer: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "flex-end",
  },
  postDetailsText: {
    textAlign: "right",
    top: 25,
    right: 20,
  },
});

export default AdsCards;
