import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  Image,
  ScrollView,
} from "react-native";
import Background from "../Component/Background";
import SelectImage from "../Component/SelectImage";
import { MaterialIcons } from "@expo/vector-icons";

const CarAdPage = () => {
  const [title, setTitle] = useState("");
  const [make, setMake] = useState("");
  const [model, setModel] = useState("");
  const [variant, setVariant] = useState("");
  const [rent, setRent] = useState("");
  const [description, setDescription] = useState("");
  const [images, setImages] = useState([]);

  const handleImagesSelected = (imageURIs) => {
    // Do something with the selected image URIs, such as storing them in state or sending them to the server
    console.log("Selected Images:", imageURIs);
  };

  const handleSubmit = () => {
    // Implement logic to handle the form submission (e.g., sending data to server/database)
    // Include fields: title, make, model, variant, rent, description, and images
    console.log("DATA=>", title, model, make, variant, rent, description);
  };

  return (
    <View>
      <Background />
      <ScrollView style={styles.container}>
        {/* Display selected images */}
        {/* Image upload section */}
        <SelectImage onImagesSelected={handleImagesSelected} />
        <Text style={styles.label}>TITLE:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setTitle(text)}
          value={title}
          placeholder="Enter Title"
        />
        <Text style={styles.label}>MAKE:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setMake(text)}
          value={make}
          placeholder="Enter Make"
        />
        <Text style={styles.label}>MODEL:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setModel(text)}
          value={model}
          placeholder="Enter Model"
        />
        <Text style={styles.label}>VARIANT:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setVariant(text)}
          value={variant}
          placeholder="Enter Variant"
        />
        <Text style={styles.label}>RENT:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setRent(text)}
          value={rent}
          placeholder="Enter Rent"
        />
        <Text style={styles.label}>DESCRIPTION:</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => setDescription(text)}
          value={description}
          placeholder="Enter Description"
        />

        {/* Submit button */}
        <TouchableOpacity style={styles.saveButton} onPress={handleSubmit}>
          <MaterialIcons name="add-box" size={24} color="black" />
          <Text style={styles.buttonText}>CREATE POST</Text>
        </TouchableOpacity>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 20,
  },
  label: {
    fontSize: 18,
    marginBottom: 5,
    fontFamily: "appfont",
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginBottom: 15,
    paddingHorizontal: 10,
    borderRadius: 5,
  },
  image: {
    width: 100,
    height: 100,
    marginRight: 10,
  },
  saveButton: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#32A1A8",
    borderRadius: 5,
    paddingVertical: 10, // Adjust vertical padding as needed
    paddingHorizontal: 20, // Adjust horizontal padding as needed
  },
  button: {
    marginLeft: 5, // Add left margin to create space between icon and text
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DABFBF",
    borderRadius: 5,
    height: 80,
    marginBottom: 5,
    paddingVertical: 10, // Adjust vertical padding as needed
    paddingHorizontal: 20, // Adjust horizontal padding as needed
  },
  buttonText: {
    marginLeft: 5, // Add left margin to create space between icon and text
    fontFamily: "appfont",
    fontSize: 18,
    color: "white",
  },
});

export default CarAdPage;
