import React, { useState } from "react";
import { StyleSheet, View, Image, TouchableOpacity, Text } from "react-native";
import ModalDropdown from "react-native-modal-dropdown";

const SellerButtons = ({ imageSource, placeholder }) => {
  return (
    <View style={styles.searchContainer}>
      <View style={styles.searchbuttonStyle}>
        <View style={styles.buttonContent}>
          <View style={styles.circle}></View>
          <Image
            source={imageSource}
            style={styles.btnImageStyle}
            resizeMode="contain"
          />
          <ModalDropdown
            options={options}
            onSelect={handleSelect}
            style={styles.dropdownContainer}
          >
            <Text style={styles.inputStyle}>
              {selectedItems.length > 0
                ? selectedItems.join(", ")
                : placeholder}
            </Text>
          </ModalDropdown>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  btnContainer: {
    alignItems: "center",
    marginTop: 20,
  },
  textStyle: {
    paddingHorizontal: 35,
    fontSize: 15,
    color: "black",
    fontWeight: "bold",
  },
  searchbuttonStyle: {
    backgroundColor: "#FFFFFF",
    display: "flex",
    flexDirection: "row",
    gap: 5,
    alignItems: "center",
    borderRadius: 26,
    width: 342,
    height: 81,
  },
  buttonContent: {
    flexDirection: "row",
    alignItems: "center",
    flex: 1,
  },
  circle: {
    width: 70,
    height: 70,
    borderRadius: 35,
    backgroundColor: "#2F2E41",
    position: "absolute",
    top: -10,
    left: 10,
  },
  btnImageStyle: {
    width: 60,
    height: 50,
    left: 10,
    bottom: 0,
  },
  iconContainer: {
    flexDirection: "row",
    marginLeft: 10,
    marginRight: 10,
    alignItems: "center",
  },
  icon: {
    marginLeft: 10,
  },
});
export default SellerButtons;
