import React, { createContext, useState, useEffect } from "react";
import { API } from "../api/config";

// CONTEXT
const PostContext = createContext();
const ApartmentPostContext = createContext();

// PROVIDERS
const PostProvider = ({ children }) => {
  const [carPosts, setCarPosts] = useState([]);

  const getAllCarPosts = async () => {
    try {
      const { data } = await API.get("/post/car/get-all-post");
      setCarPosts(data?.posts);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getAllCarPosts();
  }, []);

  return (
    <PostContext.Provider value={[carPosts, setCarPosts, getAllCarPosts]}>
      {children}
    </PostContext.Provider>
  );
};

const ApartmentPostProvider = ({ children }) => {
  const [apartmentPosts, setApartmentPosts] = useState([]);

  const getAllApartmentPosts = async () => {
    try {
      const { data } = await API.get("/post/apartment/get-all-post");
      setApartmentPosts(data?.posts);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getAllApartmentPosts();
  }, []);

  return (
    <ApartmentPostContext.Provider
      value={[apartmentPosts, setApartmentPosts, getAllApartmentPosts]}
    >
      {children}
    </ApartmentPostContext.Provider>
  );
};

export {
  PostContext,
  PostProvider,
  ApartmentPostContext,
  ApartmentPostProvider,
};
