import React, { useContext, useRef, useEffect, useState } from "react";
import SearchBar from "../Component/Home/SearchBar";
import applogo from "../../assets/apartlogo.png";
import { View, Text, StyleSheet, ScrollView } from "react-native";
import Menu from "../Component/Menu";
import Background from "../Component/Background";
import { PostContext } from "../context/postContext";
import AdsCards from "../Component/Banner/AdsCards";
import { AuthContext } from "../context/authContext";

const AppartmentPage = () => {
  const [posts] = useContext(PostContext);
  const [userIdArray] = useContext(AuthContext); // Use AuthContext to get userId
  const userId = userIdArray?.data?.user?._id;

  const searchContainerRef = useRef(null);

  useEffect(() => {
    if (searchContainerRef.current) {
      // Measure the height of the SearchBar and update the padding dynamically
      searchContainerRef.current.measure((fx, fy, width, height, px, py) => {
        setPaddingTop(height);
      });
    }
  }, [searchContainerRef]);

  const [paddingTop, setPaddingTop] = useState(0);

  return (
    <View style={styles.container}>
      <Background />
      <View style={styles.searchContainer} ref={searchContainerRef}>
        <SearchBar
          setSearchText={(value) => console.log(value)}
          imageSource={applogo}
          placeholder="SEARCH YOUR APPARTMENT"
        />
      </View>
      <Text style={styles.lengthText}>Total Posts: {posts?.length}</Text>
      <ScrollView
        style={styles.scrollContainer}
        contentContainerStyle={{ paddingTop }}
      >
        <AdsCards posts={posts} userId={userId} />
      </ScrollView>
      <View style={styles.bottomMenu}>
        <Menu />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  searchContainer: {
    position: "absolute",
    top: 120,
    width: "100%",
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
  },
  scrollContainer: {
    bottom: 50,
  },
  lengthText: {
    top: 250,
    left: 20,
  },
  bottomMenu: {
    position: "absolute",
    bottom: 20,
    left: 0,
    right: 0,
    borderTopWidth: 2,
    borderTopColor: "#DDDDDD",
  },
});

export default AppartmentPage;
