import React, { createContext, useState, useEffect } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";

//CONTEXT
const AuthContext = createContext();

//PROVIDER
const AuthProvider = ({ children }) => {
  //GLOBAL STATE
  const [state, setState] = useState({
    user: null,
    token: "",
  });

  //INITIAL LOCAL STORAGE DATA
  useEffect(() => {
    const loadLocalStorageData = async () => {
      let response = await AsyncStorage.getItem("@auth");
      let loginResponse = JSON.parse(response);

      setState({
        ...state,
        user: loginResponse?.user,
        token: loginResponse?.token,
      });
    };
    loadLocalStorageData();
  }, []);
  const loadUserDataFromStorage = async () => {
    let response = await AsyncStorage.getItem("@auth");
    let loginResponse = JSON.parse(response);

    setState({
      ...state,
      user: loginResponse?.user,
      token: loginResponse?.token,
    });
  };

  // useEffect(() => {
  //   loadUserDataFromStorage();
  // }, []);
  // let token = state && state.token;

  // //DEFAULT AXIOS SETTING
  // axios.defaults.headers.common["Authorization"] = Bearer ${token};
  // axios.defaults.baseURL = "http://127.0.0.1:8080/api/v1";

  const axios = require("axios");

  // Replace 'YOUR_API_ENDPOINT' and 'YOUR_AUTH_TOKEN' with actual values
  const API_ENDPOINT = "http://127.0.0.1:8080/api/v1";
  const AUTH_TOKEN =
    "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2NTQ1MDQ5NGRkZWQ0MDY4ZDdlMGUyNzEiLCJpYXQiOjE2OTkxNDE4NjAsImV4cCI6MTY5OTc0NjY2MH0.i7pDGJAzhg68E1BhiLUsclQmh1Tx9sVn0ztOazeTSWc";

  // Create an Axios instance with default headers
  const axiosInstance = axios.create({
    baseURL: API_ENDPOINT,
    headers: {
      Authorization: `Bearer ${AUTH_TOKEN}`,
      "Content-Type": "application/json", // You can set other headers as needed
    },
  });

  // Example GET request
  axiosInstance
    .get("/users")
    .then((response) => {
      // Handle the response
      console.log(response.data);
    })
    .catch((error) => {
      // Handle any errors
      console.error(error);
    });
  return (
    <AuthContext.Provider value={[state, setState]}>
      {children}
    </AuthContext.Provider>
  );
};

export { AuthContext, AuthProvider };
