import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  Image,
  ScrollView,
  Modal,
} from "react-native";

const MAX_FILE_SIZE = 5 * 1024 * 1024; // 5 MB (in bytes)

const SelectImage = ({ onImagesSelected }) => {
  const [images, setImages] = useState([]);
  const [modalVisible, setModalVisible] = useState(false);

  const handleFileChange = (event) => {
    const selectedFiles = event.target.files;

    if (selectedFiles.length > 0) {
      const selectedImages = Array.from(selectedFiles).map((file) =>
        URL.createObjectURL(file)
      );

      // Update the images state variable with selected image URIs
      setImages(selectedImages);
      // Invoke the callback with selected image URIs
      onImagesSelected(selectedImages);
    }
  };

  const openFilePicker = () => {
    // Programmatically trigger the file input
    const fileInput = document.getElementById("fileInput");
    fileInput.click();
  };

  return (
    <View style={styles.container}>
      <ScrollView horizontal>
        {images.map((uri, index) => (
          <Image key={index} source={{ uri }} style={styles.imagePreview} />
        ))}
      </ScrollView>

      <TouchableOpacity
        style={[styles.imagePickerButton, { backgroundColor: "#DABFBF" }]}
        onPress={openFilePicker}
      >
        <Text style={styles.buttonText}>ADD IMAGE</Text>
      </TouchableOpacity>

      <input
        type="file"
        id="fileInput"
        multiple
        style={{ display: "none" }}
        onChange={handleFileChange}
      />

      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalContainer}>
          <TouchableOpacity
            style={styles.modalButtonC}
            onPress={() => handleImagePick("camera")}
          >
            <Text style={styles.modalButtonText}>Choose From Camera</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.modalButtonC}
            onPress={() => handleImagePick("gallery")}
          >
            <Text style={styles.modalButtonText}>Choose From Gallery</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.modalButton}
            onPress={() => setModalVisible(false)}
          >
            <Text style={styles.modalButtonText}>CANCEL</Text>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  imagePickerButton: {
    marginLeft: 5, // Add left margin to create space between icon and text
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DABFBF",
    borderRadius: 5,
    marginBottom: 5,
    paddingVertical: 10, // Adjust vertical padding as needed
    paddingHorizontal: 20, // Adjust horizontal padding as needed
  },
  imagePreview: {
    width: 200,
    height: 100,
    marginVertical: 10,
    marginLeft: 70,
  },
  buttonText: {
    marginLeft: 5, // Add left margin to create space between icon and text
    fontFamily: "appfont",
    fontSize: 18,
    color: "white",
  },
  imageArea: {
    width: 200, // Set the width to an appropriate value
    height: 200, // Set the height to an appropriate value
  },
  modalContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
  modalButton: {
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#DABFBF",
    marginVertical: 10,
    height: 50,
    width: 100,
    borderRadius: 5,
  },
  modalButtonC: {
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "row", // Arrange buttons horizontally
    backgroundColor: "#DABFBF",
    marginVertical: 10,
    height: 50,
    width: 200,
    borderRadius: 5,
  },

  modalButtonText: {
    fontFamily: "appfont",
    fontSize: 18,
    color: "white",
  },
});

export default SelectImage;
