import React from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  ScrollView,
  Dimensions,
} from "react-native";

const { width, height } = Dimensions.get("window");

const PostDetailScreen = ({ route }) => {
  const { post } = route.params;

  return (
    <View style={styles.container}>
      {/* Display post images */}
      <ScrollView horizontal>
        <View style={styles.imageContainer}>
          {post?.postImages.map((image, index) => (
            <Image
              key={index}
              source={{ uri: image.url }}
              style={{ width: width, height: 200 }}
              resizeMode="cover"
            />
          ))}
        </View>
      </ScrollView>
      {/* Display detailed information about the post using the 'post' object */}
      <View style={styles.innerContainer}>
        <Text>Title: {post.title}</Text>
        <Text>Model: {post.model}</Text>
        <Text>Make: {post?.make}</Text>
        <Text>Variant: {post?.variant}</Text>
        <Text>Description: {post?.description}</Text>
        <Text>PostedBy: {post?.postedBy?.name}</Text>
        {/* ... other details ... */}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  imageContainer: {
    height: height * 0.4, // Adjust as needed
    width: width,
    backgroundColor: "#D9D9D9",
    borderRadius: 25,
    marginBottom: 20,
    marginTop: 10,
  },
  innerContainer: {
    flex: 1,
    backgroundColor: "#D9D9D9",
    borderRadius: 25,
    margin: 16,
    padding: 16,
  },
});

export default PostDetailScreen;
