import React from "react";
import { StyleSheet, View, Text, Image, Pressable } from "react-native";
import Carousel, { PaginationLight } from "react-native-x-carousel";
import moment from "moment";

const AdsBanner = ({ posts }) => {
  const renderSlider = (post, index) => {
    return (
      <View key={index} style={styles.cardContainer}>
        <View>
          <Text>Title: {post?.title}</Text>
          <Text>Make: {post?.make}</Text>
          <Text>Model: {post?.model}</Text>
          <Text>Variant: {post?.variant}</Text>
          <Text>Rent: {post?.rent}</Text>
          <Text>Description: {post?.description}</Text>
        </View>
        <View>
          <Text>PostedBy: {post?.postedBy?.name}</Text>
          <Text>CreatedAt: {moment(post?.createdAt).format("DD:MM:YYYY")}</Text>
        </View>
        <Carousel
          pagination={PaginationLight}
          renderItem={(data) => (
            <Pressable onPress={() => alert(data.id)}>
              <Image style={styles.card} source={{ uri: data.coverImageUri }} />
              <View
                style={[
                  styles.cornerLabel,
                  { backgroundColor: data.cornerLabelColor },
                ]}
              >
                <Text style={styles.cornerLabelText}>
                  {data.cornerLabelText}
                </Text>
              </View>
            </Pressable>
          )}
          data={post.bannerData}
          loop
        />
      </View>
    );
  };

  return (
    <View style={styles.container}>
      {posts.map((post, index) => renderSlider(post, index))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#D9D9D9",
  },
  cardContainer: {
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 20,
  },
  card: {
    width: 302,
    height: 161,
  },
  cornerLabel: {
    position: "absolute",
    bottom: 0,
    right: 0,
    borderTopLeftRadius: 8,
  },
  cornerLabelText: {
    fontSize: 12,
    color: "#fff",
    fontWeight: "600",
    paddingLeft: 5,
    paddingRight: 5,
    paddingTop: 2,
    paddingBottom: 2,
  },
});

export default AdsBanner;
