import React from "react";
import { StyleSheet, View, Dimensions, Text } from "react-native";
import Carousel from "react-native-x-carousel"; // Import Carousel from react-native-x-carousel
import moment from "moment";

const { width } = Dimensions.get("window");

const AdsCards = ({ posts }) => {
  return (
    <View>
      <Text>Total Posts: {posts?.length}</Text>
      <View style={styles.container}>
        {posts?.map((post, index) => (
          <View style={styles.innerContainer} key={index}>
            <Carousel
              data={post?.postImages} // Pass the postImages array to the Carousel component
              renderItem={({ item }) => (
                <View style={styles.sliderContainer}>
                  <Image
                    source={{ uri: item.url }}
                    style={styles.card}
                    resizeMode="cover"
                  />
                </View>
              )}
              itemWidth={width * 0.99}
              inActiveScale={0.94}
            />
            <View style={styles.innerTextContainer}>
              {/* ... (other post details) */}
            </View>
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#D9D9D9",
  },
  innerContainer: {
    height: 294,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25,
    marginBottom: 20,
  },
  card: {
    width: width * 0.99,
    height: width * 0.5,
    resizeMode: "cover",
    borderRadius: 8,
    overflow: "hidden",
  },
  sliderContainer: {
    backgroundColor: "#D9D9D9",
    alignItems: "center",
    justifyContent: "center",
    marginTop: 10,
  },
  innerTextContainer: {
    width: 302,
    height: 71,
    backgroundColor: "white",
    borderRadius: 25,
    left: 18,
  },
  titleHeading: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  titleContainer: {
    flexDirection: "row",
    left: 15,
    top: 10,
    fontWeight: "bold",
  },
  titlerentHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  postDetailsContainer: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "flex-end",
  },
  postDetailsText: {
    textAlign: "right",
    top: 25,
    right: 20,
  },
});

export default AdsCards;
