import React, { useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  Modal,
  TextInput,
  StyleSheet,
  Image,
} from "react-native";
import * as ImagePicker from "expo-image-picker";
import { Ionicons } from "@expo/vector-icons";
import { MaterialIcons } from "@expo/vector-icons";
import { MaterialCommunityIcons } from "@expo/vector-icons";
import axios from "axios";

const SellerButtons = ({
  setbtnText,
  imageSource,
  placeholder,
  inputs,
  postEndpoint, // API endpoint for posting data
  requiredFields, // Required fields for the specific item type
}) => {
  const [modalVisible, setModalVisible] = useState(false);
  const [values, setValues] = useState({});
  const [images, setImages] = useState([]);

  const handleImagePick = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
      multiple: true, // Enable multiple image selection
    });

    console.log("Image Picker Result:", result);

    if (!result.cancelled) {
      // Update the images state variable with selected image URIs
      setImages(result.assets.map((asset) => asset.uri));
    } else {
      // Handle the case when the user cancels image selection
      console.log("Image selection canceled");
    }

    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();

    if (status !== "granted") {
      alert("Sorry, we need camera roll permissions to make this work!");
      return;
    }
  };

  const handleSave = async () => {
    try {
      const allFieldsEntered = inputs.every((input) => values[input.key]);
      const requiredFieldsEntered = requiredFields.every(
        (field) => values[field]
      );
      if (
        allFieldsEntered &&
        Array.isArray(images) &&
        images.length > 0 &&
        requiredFieldsEntered
      ) {
        console.log("Values:", values);
        console.log("Image URIs:", images);

        // Send data to the specific API endpoint for this button
        const response = await axios.post(postEndpoint, { values, images });
        console.log("Check", response);

        setModalVisible(false);
      } else {
        if (!allFieldsEntered) {
          alert("Please enter all fields.");
        } else if (!Array.isArray(images) || images.length === 0) {
          alert("Please select at least one image.");
        } else if (!requiredFieldsEntered) {
          alert("Please fill out all required fields.");
        }
      }
    } catch (error) {
      alert(
        error.response ? error.response.data.message : "Unknown error occurred"
      );
      console.log(error);
    }
  };
  const handleCloseModal = () => {
    setModalVisible(false);
  };

  const renderInputs = () => {
    return inputs.map((input, index) => {
      // Apply a different style for the description input
      const inputStyle =
        input.key === "description" ? styles.descriptionInput : styles.input;

      return (
        <TextInput
          key={index}
          style={inputStyle}
          placeholder={input.placeholder}
          onChangeText={(text) => setValues({ ...values, [input.key]: text })}
        />
      );
    });
  };

  return (
    <View>
      <TouchableOpacity onPress={() => setModalVisible(true)}>
        <View style={styles.searchbuttonStyle}>
          <View style={styles.buttonContent}>
            <View style={styles.circle}></View>
            <Image
              source={imageSource}
              style={styles.btnImageStyle}
              resizeMode="contain"
            />
            <Text style={styles.textStyle}>{placeholder}</Text>
          </View>
        </View>
        {/* For example, you can display an image and a placeholder */}
      </TouchableOpacity>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <TouchableOpacity onPress={handleCloseModal}>
              <MaterialCommunityIcons
                name="close-box"
                size={24}
                color="black"
              />
            </TouchableOpacity>
            {images.map((uri, index) => (
              <Image key={uri} source={{ uri }} style={styles.imagePreview} />
            ))}
            {renderInputs()}
            <TouchableOpacity
              style={[styles.imagePickerButton, { backgroundColor: "#DABFBF" }]}
              onPress={handleImagePick}
            >
              <Ionicons name="add-circle" size={24} color="black" />
              <Text style={styles.imagePickerText}>ADD IMAGE</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.saveButton} onPress={handleSave}>
              <MaterialIcons name="add-box" size={24} color="black" />
              <Text style={styles.buttonText}>CREATE POST</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </View>
  );
};
const styles = StyleSheet.create({
  btnContainer: {
    alignItems: "center",
    marginTop: 20,
  },
  imagePreview: {
    width: 200,
    height: 200,
    marginVertical: 10,
    marginLeft: 30,
  },
  textStyle: {
    paddingHorizontal: 35,
    fontSize: 15,
    color: "black",
    fontWeight: "bold",
  },
  searchbuttonStyle: {
    backgroundColor: "#FFFFFF",
    flexDirection: "row",
    gap: 5,
    alignItems: "center",
    borderRadius: 26,
    width: 342,
    height: 81,
    marginTop: 40,
    marginLeft: 20,
  },
  buttonContent: {
    flexDirection: "row",
    alignItems: "center",
    flex: 1,
  },
  circle: {
    width: 70,
    height: 70,
    borderRadius: 35,
    backgroundColor: "#2F2E41",
    position: "absolute",
    top: -10,
    left: 10,
  },
  btnImageStyle: {
    width: 60,
    height: 50,
    left: 10,
    bottom: 0,
  },
  iconContainer: {
    flexDirection: "row",
    marginLeft: 10,
    marginRight: 10,
    alignItems: "center",
  },
  icon: {
    marginLeft: 10,
  },
  modalContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
  modalContent: {
    backgroundColor: "#fff",
    padding: 20,
    borderRadius: 20,
    width: 300,
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 2,
    marginVertical: 10,
    paddingHorizontal: 10,
  },

  descriptionInput: {
    height: 100, // Set the desired height for the description box
    borderColor: "gray",
    borderWidth: 2,
    marginVertical: 10,
    paddingHorizontal: 10,
  },
  saveButton: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#32A1A8",
    borderRadius: 5,
    paddingVertical: 10, // Adjust vertical padding as needed
    paddingHorizontal: 20, // Adjust horizontal padding as needed
  },

  buttonText: {
    marginLeft: 5, // Add left margin to create space between icon and text
    color: "#fff",
    fontWeight: "bold",
    fontSize: 18,
  },
  imagePickerButton: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DABFBF",
    borderRadius: 5,
    width: 260,
    height: 71,
    marginBottom: 10,
  },
  imagePickerText: {
    marginLeft: 5, // Add left margin to create space between icon and text
    fontWeight: "bold",
    fontSize: 18,
  },
  plusIcon: {
    marginLeft: 50, // Adjust marginLeft as needed to create the desired space
  },
});

export default SellerButtons;
