import React from "react";
import {
  StyleSheet,
  View,
  Dimensions,
  ScrollView,
  Text,
  Image,
} from "react-native";
import moment from "moment";

const { width } = Dimensions.get("window");

const AdsCards = ({ posts }) => {
  return (
    <View>
      <Text>Total Posts: {posts?.length}</Text>
      <View style={styles.container}>
        {posts?.map((post, index) => (
          <View style={styles.innerContainer} key={index}>
            <ScrollView
              horizontal
              showsHorizontalScrollIndicator={false}
              pagingEnabled
            >
              {post?.postImages.map((image, imageIndex) => (
                <Image
                  key={imageIndex}
                  source={{ uri: image.url }}
                  style={styles.card}
                  resizeMode="cover"
                />
              ))}
            </ScrollView>
            <View style={styles.innerTextContainer}>
              <View style={styles.titleContainer}>
                <Text style={styles.titleHeading}>{post?.title}</Text>
                <Text style={styles.titleHeading}> Model {post?.model}</Text>
                <Text style={styles.titlerentHeading}>
                  {" "}
                  | Rent: {post?.rent}
                </Text>
              </View>
              {/* <Text>Make: {post?.make}</Text> */}
              {/* <Text>Variant: {post?.variant}</Text>
                <Text>Description: {post?.description}</Text> */}
              <View style={styles.postDetailsContainer}>
                {/* <Text>PostedBy: {post?.postedBy?.name}</Text> */}
                <Text style={styles.postDetailsText}>
                  {moment(post?.createdAt).format("DD:MM:YYYY")}
                </Text>
              </View>
            </View>
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#D9D9D9",
  },
  innerContainer: {
    height: 294,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25,
    marginBottom: 20,
  },
  card: {
    width: width * 0.99,
    height: width * 0.5,
    resizeMode: "cover",
    borderRadius: 8,
    overflow: "hidden",
  },
  innerTextContainer: {
    width: 302,
    height: 71,
    backgroundColor: "white",
    borderRadius: 25,
    left: 18,
  },
  titleHeading: {
    fontSize: 18,
    fontFamily: "appfont",
  },
  titleContainer: {
    flexDirection: "row",
    left: 15,
    top: 10,
    fontWeight: "bold",
  },
  titlerentHeading: {
    fontSize: 15,
    top: 4,
    fontWeight: "bold",
  },
  postDetailsContainer: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "flex-end",
  },
  postDetailsText: {
    textAlign: "right",
    top: 25,
    right: 20,
  },
});

export default AdsCards;
