import React from "react";
import { StyleSheet, View, ScrollView, Image, Dimensions } from "react-native";

const Image1 = require("../../../assets/mainpageImage.png");
const Image2 = require("../../../assets/header.png");

const Slider = () => {
  const sliderList = [
    {
      id: 1,
      name: "Slider 1",
      imageUrl:
        "https://images.pexels.com/photos/7957748/pexels-photo-7957748.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
    {
      id: 2,
      name: "Slider 2",
      imageUrl:
        "https://img.freepik.com/free-vector/flat-design-luxury-car-dealer-sale-banner_23-2149975175.jpg?w=1380&t=st=1697456960~exp=1697457560~hmac=d421ad368a95f868ad3ee0687142fd5586dd6fa46ed33bcb2264c99ab85364fe",
    },
  ];

  return (
    <View style={styles.sliderContainer}>
      <ScrollView horizontal showsHorizontalScrollIndicator={false}>
        {sliderList.map((item) => (
          <Image
            key={item.id}
            source={{ uri: item.imageUrl }}
            style={styles.image}
          />
        ))}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  sliderContainer: {
    height: 170,
    left: 10,
    right: 10,
    /*marginBottom: 10,
    marginTop: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,*/
    top: -55,
  },
  image: {
    width: Dimensions.get("screen").width * 0.9,
    borderRadius: 5,
    margin: 2,
  },
});

export default Slider;
