import React from "react";
import SearchBar from "../Component/Home/SearchBar";
import carlogo from "../../assets/carlogo.png";
import {
  View,
  Text,
  StyleSheet,
  Image,
  ScrollView,
  Pressable,
} from "react-native";
import Menu from "../Component/Menu";
import Carousel, { PaginationLight } from "react-native-x-carousel";
import { BannerData } from "../Component/Data/BannerData";

const headlogo = require("../../assets/download.png");
const header = require("../../assets/header.png");

const CarPage = () => {
  const renderItem = ({ item }) => (
    <View key={item.id} style={styles.cardContainer}>
      <Pressable onPress={() => alert(item.id)}>
        <View style={styles.cardWrapper}>
          <Image style={styles.card} source={{ uri: item.coverImageUri }} />
          <View
            style={[
              styles.cornerLabel,
              { backgroundColor: item.cornerLabelColor },
            ]}
          >
            <Text style={styles.cornerLabelText}>{item.cornerLabelText}</Text>
          </View>
        </View>
      </Pressable>
    </View>
  );

  return (
    <View style={styles.container}>
      <View style={styles.topContainer}>
        <Image source={headlogo} style={styles.headLogo} resizeMode="contain" />
        <View style={styles.headerContainer}>
          <Image
            source={header}
            style={styles.headerImage}
            resizeMode="contain"
          />
        </View>
      </View>
      <View style={styles.searchContainer}>
        <SearchBar
          style={styles.searchStyle}
          setSearchText={(value) => console.log(value)}
          imageSource={carlogo}
          placeholder="SEARCH YOUR CAR"
        />
      </View>
      <ScrollView>
        <View style={styles.innerContainer}>
          <View style={styles.innerCarousel}>
            <Carousel
              pagination={PaginationLight}
              renderItem={renderItem}
              data={BannerData}
              loop
              autoplay
            />
          </View>
        </View>
      </ScrollView>
      <View style={styles.bottomMenu}>
        <Menu />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  topContainer: {
    flexDirection: "row",
    alignItems: "center",
  },
  headLogo: {
    width: 200,
    height: 200,
    margin: 10,
  },
  headerContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  headerImage: {
    width: 300,
    height: 70,
    left: -110,
  },
  searchContainer: {
    width: "100%",
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
  },
  bottomMenu: {
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
    borderTopWidth: 2,
    borderTopColor: "#DDDDDD",
  },
  searchStyle: {
    fontSize: 30,
  },
  innerContainer: {
    height: 294,
    width: 338,
    backgroundColor: "#D9D9D9",
    borderRadius: 25, // Remove quotes around the number
  },
  innerCarousel: {
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  cardContainer: {
    width: 200,
    marginHorizontal: 10,
  },
  cardWrapper: {
    borderRadius: 10,
    overflow: "hidden",
    elevation: 5,
  },
  card: {
    width: "100%",
    height: 250,
  },
  cornerLabel: {
    position: "absolute",
    top: 10,
    right: 10,
    backgroundColor: "red",
    paddingHorizontal: 10,
    paddingVertical: 5,
    borderRadius: 10,
  },
  cornerLabelText: {
    color: "#fff",
    fontWeight: "bold",
  },
});

export default CarPage;
