import React, { useContext } from "react";
import SearchBar from "../Component/Home/SearchBar";
import applogo from "../../assets/apartlogo.png";
import { View, Text, StyleSheet, Image, ScrollView } from "react-native";
import Menu from "../Component/Menu";
import Background from "../Component/Background";
import { PostContext } from "../context/postContext";
import AdsCards from "../Component/Banner/AdsCards";
import { AuthContext } from "../context/authContext";

const headlogo = require("../../assets/download.png");
const header = require("../../assets/header.png");

const AppartmentPage = () => {
  const [posts] = useContext(PostContext);
  const [userIdArray] = useContext(AuthContext); // Use AuthContext to get userId
  const userId = userIdArray?.data?.user?._id;

  return (
    <View style={styles.container}>
      <Background />
      <View style={styles.searchContainer}>
        <SearchBar
          setSearchText={(value) => console.log(value)}
          imageSource={applogo}
          placeholder="SEARCH YOUR APPARTMENT"
        />
      </View>
      <Text style={styles.lengthText}>Total Posts: {posts?.length}</Text>
      {/* Display the userId */}
      <ScrollView style={styles.scrollContainer}>
        <AdsCards posts={posts} userId={userId} />
      </ScrollView>
      <View style={styles.bottomMenu}>
        <Menu />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },

  searchContainer: {
    position: "absolute",
    top: 120,
    width: "100%",
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
  },
  scrollContainer: {
    height: 294,
    marginTop: -30,
  },
  lengthText: {
    top: -30,
    left: 20,
  },
  bottomMenu: {
    position: "absolute",
    bottom: 20,
    left: 0,
    right: 0,
    borderTopWidth: 2,
    borderTopColor: "#DDDDDD",
  },
});

export default AppartmentPage;
