import React from "react";
import { StyleSheet, View, Image, Dimensions, Text } from "react-native";
import Carousel from "react-native-x-carousel"; // Import Carousel without PaginationLight
import moment from "moment";

const { width } = Dimensions.get("window");

const AdsCards = ({ posts }) => {
  const renderItem = ({ item }) => (
    <View style={styles.cardContainer}>
      <Image
        source={{ uri: item.url }}
        style={styles.card}
        resizeMode="cover"
      />
    </View>
  );

  return (
    <View>
      <Text>Total Posts: {posts?.length}</Text>
      <View style={styles.container}>
        {posts?.map((post, index) => (
          <View style={styles.innerContainer} key={index}>
            <View style={styles.sliderContainer}>
              <Carousel
                renderItem={renderItem}
                data={post?.postImages}
                loop
                itemsPerPage={1}
                horizontal={true}
                sliderWidth={width}
                itemWidth={width}
              />
            </View>
            <View style={styles.innerTextContainer}>
              {/* ... (rest of your content) */}
            </View>
          </View>
        ))}
      </View>
    </View>
  );
};
