import React from "react";
import {
  StyleSheet,
  View,
  Text,
  Image,
  Dimensions,
  Pressable,
} from "react-native";
import Carousel, { PaginationLight } from "react-native-x-carousel";
import { BannerData } from "../Data/BannerData";

const { width } = Dimensions.get("window");
const innerContainerWidth = 338; // Width of your inner container
const innerContainerHeight = 294; // Height of your inner container

const AdsBanner = ({ posts }) => {
  const renderItem = (data) => (
    <View key={data.coverImageUri} style={styles.cardContainer}>
      {BannerData.map((item, index) => {
        const post = posts[index]; // Get the corresponding post data
        return (
          <View key={index} style={styles.cardWrapper}>
            <View>
              <Text>Title: {post?.title}</Text>
              <Text>Make: {post?.make}</Text>
              <Text>Model: {post?.model}</Text>
              <Text>Variant: {post?.variant}</Text>
              <Text>Rent: {post?.rent}</Text>
              <Text>Description: {post?.description}</Text>
            </View>
            <View>
              {/* NotWoking */}
              <Text>PostedBy: {post?.postedBy?.name}</Text>
              <Text>
                CreatedAt: {moment(post?.createdAt).format("DD:MM:YYYY")}
              </Text>
            </View>
            <Pressable onPress={() => alert(item.id)}>
              <Image style={styles.card} source={{ uri: item.coverImageUri }} />
              <View
                style={[
                  styles.cornerLabel,
                  { backgroundColor: item.cornerLabelColor },
                ]}
              >
                <Text style={styles.cornerLabelText}>
                  {item.cornerLabelText}
                </Text>
              </View>
            </Pressable>
          </View>
        );
      })}
    </View>
  );

  return (
    <View style={[styles.container, styles.innerContainer]}>
      <View style={styles.innerCarousel}>
        <Carousel
          pagination={PaginationLight}
          renderItem={renderItem}
          data={BannerData}
          loop
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#D9D9D9",
  },
  innerContainer: {
    width: innerContainerWidth,
    height: innerContainerHeight,
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 25, // Apply the border radius here to match your inner container
    overflow: "hidden",
  },
  innerCarousel: {
    backgroundColor: "#D9D9D9", // Set the background color here for the Carousel
    alignItems: "center",
    justifyContent: "center",
    marginTop: 10,
  },
  cardContainer: {
    alignItems: "center",
    justifyContent: "center",
    width,
  },
  cardWrapper: {
    borderRadius: 8,
    overflow: "hidden",
  },
  card: {
    width: 302,
    height: 161,
  },
  cornerLabel: {
    position: "absolute",
    bottom: 0,
    right: 0,
    borderTopLeftRadius: 8,
  },
  cornerLabelText: {
    fontSize: 12,
    color: "#fff",
    fontWeight: "600",
    paddingLeft: 5,
    paddingRight: 5,
    paddingTop: 2,
    paddingBottom: 2,
  },
});

export default AdsBanner;
