import React, { createContext, useState, useEffect } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";

//CONTEXT
const AuthContext = createContext();

axios.defaults.baseURL = "http://127.0.0.1:8080/api/v1";

const AuthProvider = ({ children }) => {
  //GLOBAL STATE
  const [state, setState] = useState({
    user: null,
    token: "",
  });

  //INITIAL LOCAL STORAGE DATA
  useEffect(() => {
    const loadLocalStorageData = async () => {
      let response = await AsyncStorage.getItem("@auth");
      let loginResponse = JSON.parse(response);

      setState({
        ...state,
        user: loginResponse?.user,
        token: loginResponse?.token,
      });

      // Set the Authorization header for Axios requests
      if (loginResponse?.token) {
        axios.defaults.headers.common[
          "Authorization"
        ] = `Bearer ${loginResponse.token}`;
      }
    };

    loadLocalStorageData();
  }, []); // Empty dependency array ensures useEffect runs once after the initial render

  return (
    <AuthContext.Provider value={[state, setState]}>
      {children}
    </AuthContext.Provider>
  );
};

export { AuthContext, AuthProvider };
