import React, { useEffect, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  Image,
  Dimensions,
  Pressable,
  ActivityIndicator,
} from "react-native";
import Carousel, { PaginationLight } from "react-native-x-carousel";
import axios from "axios";

const { width } = Dimensions.get("window");
// Define renderItem function

const Banner = () => {
  const [banner, setBanner] = useState();

  useEffect(() => {
    (async function getBannerImages() {
      try {
        const response = await axios.get(
          "http://127.0.0.1:8080/api/v1/banner/get"
        );

        if (response.status === 200) {
          setBanner(response.data);
        }
      } catch (error) {
        console.error("Error fetching banner data:", error);
      }
    })();
  }, []);
  console.log(banner);
  if (!banner) {
    // Data is still being fetched, show loading indicator or placeholder
    return <ActivityIndicator size="large" />;
  }
  const renderItem = (data) => (
    <View key={data.coverImageUri} style={styles.cardContainer}>
      <Pressable onPress={() => alert(data._id)}>
        <View style={styles.cardWrapper}>
          <Image style={styles.card} source={{ uri: data.coverImageUri }} />
          <View
            style={[
              styles.cornerLabel,
              { backgroundColor: data.cornerLabelColor },
            ]}
          >
            <Text style={styles.cornerLabelText}>{data.cornerLabelText}</Text>
          </View>
        </View>
      </Pressable>
    </View>
  );
  return (
    <View style={styles.container}>
      <Carousel
        pagination={PaginationLight}
        renderItem={renderItem}
        data={banner} // Pass the banner data to the Carousel component
        loop
        autoplay
      />
      {banner.map((data) => (
        <Image
          key={data._id}
          source={{ uri: data.images[0].url }}
          style={styles.bannerImage}
        />
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  bannerImage: {
    width: 200, // Set the width of the images
    height: 200, // Set the height of the images
  },
  cardContainer: {
    alignItems: "center",
    justifyContent: "center",
    width,
  },
  cardWrapper: {
    borderRadius: 8,
    overflow: "hidden",
  },
  card: {
    width: width * 0.9,
    height: width * 0.4,
  },
  cornerLabel: {
    position: "absolute",
    bottom: 0,
    right: 0,
    borderTopLeftRadius: 8,
  },
  cornerLabelText: {
    fontSize: 12,
    color: "#fff",
    fontWeight: "600",
    paddingLeft: 5,
    paddingRight: 5,
    paddingTop: 2,
    paddingBottom: 2,
  },
});
export default Banner;
